};

module.exports = {

  saveAnnoucement(req, res) {
    Logger.debug('AnnouncementController.saveAnnoucement');
    const announcementData = req.body.data;
    const args = [
      {
        name: 'Title',
        value: announcementData.title,
        validations: [{ validation: 'notEmpty', message: sails.config.ValidationMessages.announcementTitleRequiredValidation }],
      }, {
        name: 'Content',
        value: announcementData.content,
        validations: [{ validation: 'notEmpty', message: sails.config.ValidationMessages.announcementContentRequiredValidation }],
      },]
      if (!announcementData) {
        res.send({ status: 300, message: 'Server error. Please try again.' });
      } else {
     ValidateService.validate(args, (validationErr, validationErrMsgs) => {
      if (validationErr) {
        Logger.error(`AnnouncementController.saveAnnoucement at ValidateService.validate ${validationErr}`);
        res.send({ status: 300, message: sails.config.serverMessage.serverError });
      } else if (validationErrMsgs.length === 0) {
          AnnouncementService.saveAnnouncement(announcementData, (err, savedAnnouncement) => {
            if (err) {
              console.log(err);
              res.send({ status: 300, message: 'Server error. Please try again.' });
            } else {
              res.send({ status: 200, message: 'Announcement addedd successfully', announcementData: savedAnnouncement });
            }
          });

      } else {
        ValidateService.getValidateMsg(validationErrMsgs, (getValidateMsgErr, errMsgs) => {
          if (getValidateMsgErr) {
            Logger.error(`AnnouncementController.saveAnnoucement at ValidateService.getValidateMsg ${getValidateMsgErr}`);
            res.send({ status: 300, message: sails.config.serverMessage.serverError });
          } else {
            Logger.verbose(errMsgs);
            res.send({ status: 422, message: errMsgs });
          }
        });
      }
    });
  }

  },

  getAnnouncementById(req, res) {
    Logger.debug('AnnouncementController.getAnnouncement');
    const announcementId = req.body.announcementId;
    Logger.verbose(announcementId);
    if (!announcementId) {
      res.send({ status: 300, message: 'Server error. Please try again.' });
    } else {
      AnnouncementService.getAnnouncement(announcementId, (err, announcementData) => {
        if (err) {
          res.send({ status: 300, message: 'Server error. Please try again.' });
        } else {
          res.send({ status: 200, announcementData });
        }
      });
    }
  },
  getAnnouncement(req, res) {
    Logger.debug('AnnouncementController.getAnnouncement');

      AnnouncementService.getAllAnnouncement((err, announcementData) => {
        if (err) {
          res.send({ status: 300, message: 'Server error. Please try again.' });
        } else {
          res.send({ status: 200, announcementData });
        }
      });

  },

  updateAnnouncement(req, res) {
    Logger.debug('AnnouncementController.updateAnnouncement');
    const announcementData = req.body.data;
    Logger.verbose(announcementData);
    if (!announcementData.id) {
      res.send({ status: 300, message: 'Server error. Please try again.' });
    } else {
      const announcementId = announcementData.id;
      AnnouncementService.updateAnnouncement(announcementId, announcementData, (err, updatedAnnouncement) => {
        if (err) {
          res.send({ status: 300, message: 'Server error. Please try again.' });
        } else {
          res.send({ status: 200, message: 'Announcement details saved successfully', announcementData: updatedAnnouncement });
        }
      });
    }
  },

  deleteAnnouncement(req, res) {
    Logger.debug('AnnouncementController.deleteAnnouncement');
    const announcementId = req.body.announcementId;
    Logger.verbose(announcementId);
    if (!announcementId) {
      res.send({ status: 300, message: 'Server error. Please try again.' });
    } else {
      AnnouncementService.updateAnnouncement(announcementId, { isActive: false }, (err) => {
        if (err) {
          res.send({ status: 300, message: 'Server error. Please try again.' });
        } else {
          res.send({ status: 200, message: 'Announcement deleted successfully' });
        }
      });
    }
  },
};

