module.exports = {
  updateProposal(proposalId, proposalData, callback) {
    Logger.debug('ApplicationService.updateProposal');
    // delete proposalData.proposalId;
    Logger.verbose(proposalId);
    Logger.verbose(proposalData);
    Logger.debug('InvestigatorService.registration');
    Application.findActiveByCriteria({ proposalId }, (err, proposal) => {
      if (err) {
        Logger.error(`InvestigatorService.registration at InvestigatorService.findActiveByCriteria ${err}`);
        callback(err);
      } else if (proposal) {
        Application.updateActiveById(proposalId, proposalData, (error, updatedProposal) => {
          if (err) {
            Logger.error(`ApplicationService.updateProposal at Application.updateActiveById ${err}`);
            callback(err);
          } else {
            Logger.info('Application details updated successfully.');
            callback(null, updatedProposal);
          }
        });
      } else {
        console.log('investigator', proposal);
        Logger.warn('proposal id not present.');
        callback('Not Present');
      }
    });
  },
};

module.exports = {

  tableName: 'fijihrp_announcement',

  attributes: {
    isActive: {
      type: 'boolean',
      defaultsTo: true,
    },
    title: {
      type: 'string',
      defaultsTo: '',
    },
    content: {
      type: 'string',
      defaultsTo: '',
    },
  },
  customToJSON() {
    return _.omit(this, ['createdAt','updatedAt','id']);
  },

  findByCriteria(criteria, callback) {
    Announcement.find(criteria).exec((err, data) => {
      callback(err, data);
    });
  },

  findActiveById(id, callback) {
    Announcement.findOne({ id, isActive: true }).exec((err, data) => {
      callback(err, data);
    });
  },

  findActiveByCriteria(criteria, callback) {
    Announcement.findOne(criteria).exec((err, data) => {
      callback(err, data);
    });
  },

  findActivesByCriteria(criteria, callback) {
    Announcement.find(criteria).exec((err, data) => {
      callback(err, data);
    });
  },

  updateActiveById(id, params, callback) {
    Announcement.update({ id, isActive: true }, params).fetch().exec((err, data) => {
      callback(err, data);
    });
  },

  createAnnouncement(params, callback) {
    Announcement.create(params).fetch().exec((err, data) => {
      callback(err, data);
    });
  },

  findByPaginate(criteria, projection, skipNo, limitNo, sortColumn, callback) {
    Announcement.find(criteria, projection).skip(skipNo).limit(limitNo).sort(sortColumn)
      .exec((err, data) => {
        callback(err, data);
      });
  },

};

