module.exports = {

  registration(registrationData, callback) {
    Logger.debug('InternalUserService.registration');
    InternalUser.findActiveByCriteria({ email: registrationData.email, isActive: true }, (err, internalUser) => {
      if (err) {
        Logger.error(`InternalUserService.registration at InternalUserService.findActiveByCriteria ${err}`);
        callback(err);
      } else if (internalUser) {
        Logger.warn('InternalUser already present.');
        callback('Already Present');
      } else {
        delete registrationData.confirmPassword;
        InternalUser.create(registrationData).fetch().exec((createErr, internalUserData) => {
          if (createErr) {
            Logger.error(`InternalUserService.registration at InternalUser.create ${createErr}`);
            callback(createErr);
          } else {
            Logger.info('InternalUser details saved successfully.');
            Logger.verbose(internalUserData);
            callback(null, internalUserData);
          }
        });
      }
    });
  },

  updateInternalUser(internalUserId, internalUserData, callback) {
    Logger.debug('InternalUserService.updateProposal');
    delete internalUserData.id;
    InternalUser.updateActiveById(internalUserId, internalUserData, (err, updatedInternalUser) => {
      if (err) {
        Logger.error(`InternalUserService.updateProposal at InternalUser.updateActiveById ${err}`);
        callback(err);
      } else {
        Logger.info('InternalUser details updated successfully.');
        callback(null, updatedInternalUser);
      }
    });
  },

  getInternalUser(internalUserId, callback) {
    Logger.debug('InternalUserService.getInternalUser');
    console.log('internalUserId', internalUserId);
    InternalUser.findActiveById(internalUserId, (err, internalUserData) => {
      if (err) {
        Logger.error(`InternalUserService.getInternalUser at InternalUser.findActiveById ${err}`);
        callback(err);
      } else {
        callback(null, internalUserData);
      }
    });
  },

  sendInvite(internalUserData, callback) {
    internalUserData.email = internalUserData.email.toLowerCase();
    internalUserData.role = parseInt(internalUserData.role, 10);
    console.log('internalUserData.email in service', internalUserData.email);
    console.log('internalUserData.role in service', internalUserData.role);
    console.log('internalUserData in service', internalUserData);

    Logger.debug('InternalUserService.sendInvite');
    InternalUser.findActiveByCriteria({ email: internalUserData.email, isActive: true }, (err, internalUser) => {
      if (err) {
        Logger.error(`InternalUserService.sendInvite at InternalUser.findActiveByCriteria ${err}`);
        callback(err);
      } else if (internalUser) {
        Logger.warn('Internal User already present.');
        callback('Already Present');
      } else {
        InternalUser.create(internalUserData).fetch().exec((createErr, internalUser) => {
          if (createErr) {
            Logger.error(`InternalUserService.sendInvite at InternalUser.create ${createErr}`);
            callback(createErr);
          } else {
            Logger.info('InternalUser details saved successfully.');
            EmailService.sendInviteEmailToInternalUser(internalUser);
            Logger.verbose(internalUser);
            callback(null, internalUser);
          }
        });
      }
    });
  },

  getAllInternalUser(pageNo, limit, search, callback) {
    Logger.debug('InternalUserService.getInternalUser');
    const searchTable = { where: { isActive: true, role: { '!=': 1 } } };
    const skip = (pageNo - 1) * limit;
    if (search !== '' && search !== undefined) {
      const searchObj = [{ firstName: { contains: search } }];
      searchTable.where.or = searchObj;
    }
    InternalUser.countByCriteria(searchTable, (error, count) => {
      if (error) {
        callback(error);
      } else {
        InternalUser.findByPagination(searchTable, skip, limit, (err, internalUserData) => {
          if (err) {
            Logger.error(`InternalUserService.getInternalUser at InternalUser.findActiveById ${err}`);
            callback(err);
          } else {
            callback(null, internalUserData, count);
          }
        });
      }
    });
  },

