        const committeeMemberIds = [];
        for (let i = 0; i < committeeMemberData.length; i++) {
          committeeMemberIds.push(committeeMemberData[i].id);
        }
        const unionIds = _.union(internalUserIds, committeeMemberIds);
        const uniqueIds = _.uniq(unionIds);
        if (uniqueIds.length > 10) {
          callback('You can not add more than 10 member to create commitee');
        } else {
          InternalUser.updateActiveById(uniqueIds, { isCommitteeMember: true }, (err, updatedInternalUser) => {
            if (err) {
              Logger.error(`InternalUserService.addCommiteeMember at InternalUser.updateActiveById ${err}`);
              callback(err);
            } else {
              Logger.info('InternalUser details updated successfully.');
              callback(null, updatedInternalUser);
            }
          });
        }
      }
    });
  },
};

