    ];
    return columns.map((item, index) => {
      return {
        title: item.title,
        dataIndex: item.dataIndex,
        key: item.key,
      };
    });
  }

  handleButtonClick() {
    const {
  data, key, members, committeeLength, keyLength,
  } = this.state;

     const errorLength = 10 - committeeLength - keyLength;

     const checkLimit = 10 - committeeLength - key.length;

     if (checkLimit < 0) {
       if (errorLength === 0) {
        toast.error(AllMessage.CommitteeLengthLimit, {
          position: 'top-center',
          autoClose: 5000,
          hideProgressBar: true,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: false,
          });
       } else if (errorLength !== 0) {
        toast.error(AllMessage.ErrorLengthOfCommittee1 + errorLength
          + AllMessage.ErrorLengthOfCommittee2, {
          position: 'top-center',
          autoClose: 5000,
          hideProgressBar: true,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: false,
          });
         }
     } else if (checkLimit >= 0) {
       const checkedData = data.filter((data1) => {
       return key.includes(data1.id);
       });
         members.push(checkedData);


         const removeMemberFromAllMembers = data.filter((data1) => {
         return !key.includes(data1.id);
         });

         this.setState({
             members,
             data: removeMemberFromAllMembers,

             keyLength: key.length,

       });
     }
   }


  handleRemoveClick(key) {
    const {
 members, data, originalData, keyLength,
} = this.state;

    const memberToRemove = members.flatMap((x) => { return x; });
    const removeSelectedMember = memberToRemove.filter((removeMember) => {
      return !key.includes(removeMember.id);
      });
      const addUnselectedMemberToMainTable = originalData.filter((unselectedMember) => {
        return key.includes(unselectedMember.id);
        });

        const afterDeleteKeys = keyLength - addUnselectedMemberToMainTable.length;

        data.push(addUnselectedMemberToMainTable);

        const AddMemeberConvertingArray = data.flatMap((x) => { return x; });

        this.setState({
          members: removeSelectedMember,
          data: AddMemeberConvertingArray,
           key: [],
          keyLength: afterDeleteKeys,
        });
    }

  dataFromAboveDataTable() {
    const { members } = this.state;
    const membersToAdd = members.flatMap((x) => { return x; });
    return membersToAdd.map((item, index) => {
      return {
        key: index,
        name: `${item.firstName } ${ item.lastName}`,
        email: item.email,
        status: item.status,
       action: <Tooltip placement="top" title="Remove"><Icon onClick={() => { return this.handleRemoveClick(item.id); }} style={{ marginRight: '15px', fontSize: '17px', color: 'red' }} type="close-circle" /></Tooltip>,
      };
    });
  }

  handleAddCommittee() {
    this.state.disabledButton = true;
    this.setState({
      disabledButton: true,
    });
    const { members } = this.state;

    const finalKeys = [];
    const dd = members.flatMap((x) => { return x; });


    for (let i = 0; i < dd.length; i += 1) {
      finalKeys.push(dd[i].id);
    }


    fetch(`${URL.APIURL}/addCommittee`, {
      method: 'POST',
      headers: {
        Accept: 'application/json',
        'Content-Type': 'application/json',
        'Access-Control-Allow-Origin': '*',
      },
      credentials: 'include',
      body: JSON.stringify({
        internalUserIds: finalKeys,
       }),
})
      .then((res) => { return res.json(); })
      .then(
        (res) => {
          if (res.status === 200) {
            toast.success(res.message, {
              position: 'top-center',
              autoClose: 5000,
              hideProgressBar: true,
              closeOnClick: true,
              pauseOnHover: true,
              draggable: false,
              });

          this.setState({
            internalUserData: res.internalUserData,
          });

           this.props.history.push('/manageCommittee');
           } else if (res.status === 300) {
            toast.error(res.message, {
              position: 'top-center',
              autoClose: 5000,
              hideProgressBar: true,
              closeOnClick: true,
              pauseOnHover: true,
              draggable: false,
              });
     } else if (res.status === 401) {
            error({
              title: `Session expired.${res.message}`,
              content: '',
              onOk() {
                that.props.history.push('/signIn');
              },

            });
          }
        },

);
  }

  renderTableData() {
    const { data, committeeMembers } = this.state;
    const membersKeys = [];

    const membersWithoutArray = committeeMembers.flatMap((x) => { return x; });
    for (let i = 0; i < membersWithoutArray.length; i += 1) {
      membersKeys.push(membersWithoutArray[i].id);
    }

    // var dataKeys =[];
    // for(var j=0;j<data.length;j++){
    //   dataKeys.push(data[j].id)
    // }

    const finalData = data.filter((dataToRender) => {
      return !membersKeys.includes(dataToRender.id);
      });

        // this.setState({
        //   finalData:finalData.length
        // })
    return finalData.map((item, i) => {
      return {
        key: item.id,
        name: `${item.firstName } ${ item.lastName}`,
        email: item.email,
        expertise: item.expertise === undefined ? <span>N/A</span> : item.expertise,
        add: <Checkbox ref={`ref_${ i}`} onClick={() => { return this.handleCheckBoxClick(item.id, i); }} />,
     };
    });
  }

  render() {
    document.title = `  Add Member | ${AllMessage.PageTitle}`;

    return (
      <div>
        <BreadcrumbDemo propertyProps={this.props} />
        <Card className="cardcss" title="Eligible Users">
          <div className="gx-mb-3" />
          <Table
            className="gx-table-responsive"
            columns={this.handle()}
            dataSource={this.renderTableData()}
          />
        </Card>

        <Card
          className="cardcss"
          title="Added Members"
          extra={(
            <span>
Number of members to be added :
              <b>{10 - this.state.committeeLength - this.state.keyLength}</b>
            </span>
)}
        >
          <Table
            className="gx-table-responsive"
            columns={addedMember}
            dataSource={this.dataFromAboveDataTable()}
            pagination={false}
          />
          <br />

          <Button type="primary" disabled={this.state.disabledButton} onClick={this.handleAddCommittee}>Add Committee Member(s)</Button>

        </Card>
      </div>
    );
  }
}


