const { error } = Modal;
let that;

const columns = [
  {
 title: <div style={{ fontSize: 14 }}><b>Name</b></div>, dataIndex: 'name', key: 'name', ellipsis: true,
},
  {
 title: <div style={{ fontSize: 14 }}><b>Role</b></div>, dataIndex: 'role', key: 'role', ellipsis: true,
},
  {
 title: <div style={{ fontSize: 14 }}><b>Status</b></div>, dataIndex: 'status', key: 'status', ellipsis: true,
},
  {
 title: <div style={{ fontSize: 14 }}><b>Action</b></div>, dataIndex: 'action', key: 'action',
},
];
export class UserManagementCoordinator extends Component {
  constructor(props) {
    super(props);

    this.state = {
      data: [],
     };
    that = this;
    this.handleButtonClick = this.handleButtonClick.bind(this);
  }


  componentDidMount() {
    fetch(`${URL.APIURL}/getInternalUsersExceptCordinator`, {
      method: 'POST',
      headers: {
        Accept: 'application/json',
        'Content-Type': 'application/json',
        'Access-Control-Allow-Origin': '*',
      },
      credentials: 'include',
      body: JSON.stringify({
      }),
    })
      .then((res) => {
        if (res.status === 500) {
          toast.error('server error occured', {
            position: 'top-center',
            autoClose: 5000,
            hideProgressBar: true,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: false,
            });
        }
        return res.json();
})
      .then(
        (res) => {
          if (res.status === 200) {
            this.setState({
              data: res.internalUserData,
            });
          } else if (res.status === 300) {
            toast.error(res.message, {
              position: 'top-center',
              autoClose: 5000,
              hideProgressBar: true,
              closeOnClick: true,
              pauseOnHover: true,
              draggable: false,
              });
          } else if (res.status === 401) {
            // message.error('session expired.'+res.message);
            error({
              title: AllMessage.SessionExpired + res.message,
              content: '',
              onOk() {
                that.props.history.push('/signIn');
              },

            });
          } else if (res.status === 500) {
            this.props.history.push('/serverError');
          } else if (res.status === 400) {
            // bad request
            this.props.history.push('/badRequest');
          } else if (res.status === 403) {
            // forbidden request
            this.props.history.push('/forbiddenRequest');
          }
        },
        (error1) => {
          toast.error(error1, {
            position: 'top-center',
            autoClose: 5000,
            hideProgressBar: true,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: false,
            });
},
      );
  }


  handleButtonClick() {
    this.props.history.push('/inviteUser');
  }

  renderTableData() {
    const { data } = this.state;
    return data.map((item, index) => {
      let role;
      if (item.role === 3) {
        role = 'Internal reviewer';
      } else if (item.role === 4) {
        role = 'External reviewer';
      } else if (item.role === 5) {
        role = 'Chair person';
      } else {
        role = item.role;
      }
      return {
        key: index,
        name: `${item.firstName } ${ item.lastName}`,
        role,
        status: item.isVerified === true ? <Tag color="#13c2c2">Verified</Tag> : <Tag color="#f50">Pending</Tag>,
        action: <a href={`/viewUser?id=${ item.id}`}><span className="gx-link">View</span></a>,
        description: <DataForDescription dataItem={item} />,

      };
    });
  }

  render() {
    document.title = `User Management | ${AllMessage.PageTitle}`;

    return (
      <div>
        <BreadcrumbDemo propertyProps={this.props} />

        <Card className="cardcss" title={<b>All Users</b>} extra={<Button onClick={this.handleButtonClick} type="primary">Invite User</Button>}>
          {this.state.data.length === 0 ? <Skeleton /> : (
            <Table
              className="gx-table-responsive"
              columns={columns}
              expandedRowRender={(record) => {
              return (
                <p style={{ margin: 0 }}>
                  {record.description}

                </p>
              );
              }}
              dataSource={this.renderTableData()}
            />
          )}
          <Modal
            visible={this.state.visible}
            title="Add Comments"
            onOk={this.handleOk}
            onCancel={this.handleCancel}
            footer={[
              <Button key="back" onClick={this.handleCancel}>Cancel</Button>,
              <Button key="submit" type="primary" onClick={this.handleOk}>
                Mark Complete
              </Button>,
            ]}
          >
            <TextArea rows="4" />
          </Modal>

          <Modal
            visible={this.state.markIncomplete}
            title="Add Comments"
            onOk={this.handleOk}
            onCancel={this.handleCancel}
            footer={[
              <Button key="back" onClick={this.handleCancelForMarkIncomplete}>Cancel</Button>,
              <Button key="submit" type="primary" onClick={this.handleOkForMarkIncomplete}>
                Mark Incomplete
              </Button>,
            ]}
          >
            <TextArea rows="4" />
          </Modal>
        </Card>
      </div>
    );
  }
}
export default UserManagementCoordinator;

