import React, { Component } from 'react';
import {
  Button, Form, Input, message, Modal,
} from 'antd';
import { toast } from 'react-toastify';
import NewProposal from '.';
import URL from '../../env/environmentVar';

import AllMessage from '../AllMessages';
import 'react-toastify/dist/ReactToastify.css';
import Message from '../../../../config/ValidationMessages';
import { connect } from 'react-redux';
import { withRouter } from 'react-router-dom';

let proposalId;
const urlParams = new URLSearchParams(window.location.search);
const { error } = Modal;
let that;
const id = urlParams.get('id');
const FormItem = Form.Item;
const { TextArea } = Input;
class Tab2 extends Component {
  constructor(props) {
    super(props);
    this.state = {
      title: '',
      background: '',
      objectives: '',
      studyMethods: '',
      expectedOutcomes: '',
      keywords: '',
      proposalFormid: '',
      disabledButton: false,
    };
    that = this;
  }

  componentDidMount() {
    fetch(`${URL.APIURL}/getProposal`, {
      // mode :'no-cors',
      method: 'POST',
      headers: {
        Accept: 'application/json',
        'Content-Type': 'application/json',
        'Access-Control-Allow-Origin': '*',
      },
      credentials: 'include',
      // proposalId: this.state.id,
      body: JSON.stringify({
        proposalId: id,
      }),
    })
      .then((res) => { return res.json(); })
      .then(
        (res) => {
          if (res.status === 200) {
          this.setState({
            title: res.proposalData.title,
            background: res.proposalData.background,
            objectives: res.proposalData.objectives,
            studyMethods: res.proposalData.studyMethods,
            expectedOutcomes: res.proposalData.expectedOutcomes,
            keywords: res.proposalData.keywords,
            proposalFormid: res.proposalData.proposalId,

          });
        } else if (res.status === 300) {
          console.log(res.notUserProposal);
          if (res.notUserProposal) {
            error({
              title: `${res.message}`,
              onOk() {
                that.props.history.push('/manageProposal');
              },

            });
             } else {
          toast.error(res.message, {
            position: 'top-center',
            autoClose: 5000,
            hideProgressBar: true,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: false,
            });
}
} else if (res.status === 500) {
  this.props.history.push('/serverError');
} else if (res.status === 400) {
  // bad request
  this.props.history.push('/badRequest');
} else if (res.status === 403) {
  // forbidden request
  this.props.history.push('/forbiddenRequest');
}
        },


      );
  }


  handleChange = (e) => {
    this.setState({
      [e.target.name]: e.target.value,
    });
  }

  handleSubmit = (e) => {
    e.preventDefault();
    this.setState({
      disabledButton: false,
    });

    this.props.form.validateFields((err, values) => {
      if (!err) {
        this.setState({
          disabledButton: true,
        });
        values.isProposal = true;
        values.proposalId = id;


        fetch(`${URL.APIURL}/saveProposal`, {
          // mode :'no-cors',
          method: 'POST',
          headers: {
            Accept: 'application/json',
            'Content-Type': 'application/json',
            'Access-Control-Allow-Origin': '*',
          },
          credentials: 'include',
          body: JSON.stringify({
            data: values,
          }),
        })
          .then((res) => { return res.json(); })
          .then(
            (res) => {
              if (res.status === 200) {
                this.state.disabledButton = false;
                this.setState({
                  disabledButton: false,
                });
                if (res.proposalData[0].proposalId) {
                    proposalId = res.proposalData[0].proposalId;
                  this.props.sendData(proposalId);
                  // this.setState({
                  //   id: res.proposalData[0].id,
                  // });
                }

                NewProposal.callback('3', { tab2: true });
                toast.success(res.message, {
                  position: 'top-center',
                  autoClose: 5000,
                  hideProgressBar: true,
                  closeOnClick: true,
                  pauseOnHover: true,
                  draggable: false,
                  });
} else if (res.status === 300) {
                this.setState({
                  disabledButton: false,
                });
                toast.error(res.message, {
                  position: 'top-center',
                  autoClose: 5000,
                  hideProgressBar: true,
                  closeOnClick: true,
                  pauseOnHover: true,
                  draggable: false,
                  });
} else if (res.status === 500) {
  this.props.history.push('/serverError');
} else if (res.status === 400) {
  // bad request
  this.props.history.push('/badRequest');
} else if (res.status === 403) {
  // forbidden request
  this.props.history.push('/forbiddenRequest');
}
            },


          );
      }
    });
  }

  render() {
    const { getFieldDecorator } = this.props.form;

    const formItemLayout = {
      labelCol: {
        xs: { span: 24 },
        sm: { span: 5 },
      },
      wrapperCol: {
        xs: { span: 24 },
        sm: { span: 12 },
      },
    };

    return (
      <div>
        <Form onSubmit={this.handleSubmit}>

          <FormItem
            label=" Title"
            {...formItemLayout}
          >
            {getFieldDecorator('title', {
              initialValue: this.state.title,
              rules: [{
                required: true,
                message: Message.ValidationMessages.titleRequiredValidation,
              }, {
                max: 100, message: Message.ValidationMessages.titleMaxValidation,
              }, {
                pattern: new RegExp('^[a-zA-Z0-9-,]+(\s{0,1}[a-zA-Z0-9-, ])*$'),
                message: Message.ValidationMessages.alphabetNumberPatternValidation,
                }],
            })(
              <TextArea setFieldsValue={this.state.title} onChange={this.handleChange} name="title" rows={2} placeholder="Please input your title" />,
            )}
          </FormItem>

          <FormItem
            label="Background"
            {...formItemLayout}
          >
            {getFieldDecorator('background', {
              initialValue: this.state.background,
              rules: [{
                required: true,
                message: Message.ValidationMessages.backgroundRequiredValidation,
              }, {
                max: 200, message: Message.ValidationMessages.backgroundMaxValidation,
              }, {
                pattern: new RegExp('^[a-zA-Z0-9-,]+(\s{0,1}[a-zA-Z0-9-, ])*$'),
                message: Message.ValidationMessages.alphabetNumberPatternValidation,
                }],
            })(
              <TextArea name="background" onChange={this.handleChange} setfieldsvalue={this.state.background} onChange={this.handleChange} rows={3} placeholder="Please input your background" />,
            )}
          </FormItem>

          <FormItem
            label="Objectives"
            {...formItemLayout}
          >
            {getFieldDecorator('objectives', {
              initialValue: this.state.objectives,
              rules: [{
                required: true,
                message: Message.ValidationMessages.objectiveRequiredValidation,
              }, {
                max: 300, message: Message.ValidationMessages.objectiveMaxValidation,
              }, {
                pattern: new RegExp('^[a-zA-Z0-9-,]+(\s{0,1}[a-zA-Z0-9-, ])*$'),
                message: Message.ValidationMessages.alphabetNumberPatternValidation,
                }],
            })(
              <TextArea onChange={this.handleChange} name="objectives" setfieldsvalue={this.state.objectives} rows={4} placeholder="Please input your objectives" />,
            )}
          </FormItem>

          <FormItem
            label="Study Methods"
            {...formItemLayout}
          >
            {getFieldDecorator('studyMethods', {
              initialValue: this.state.studyMethods,
              rules: [{
                required: true,
                message: Message.ValidationMessages.studyMethodsRequiredValidation,
              }, {
                max: 300, message: Message.ValidationMessages.studyMethodsMaxValidation,
              }, {
                pattern: new RegExp('^[a-zA-Z0-9-,]+(\s{0,1}[a-zA-Z0-9-, ])*$'),
                message: Message.ValidationMessages.alphabetNumberPatternValidation,
                }],
            })(
              <TextArea onChange={this.handleChange} name="studyMethods" setfieldsvalue={this.state.studyMethods} onChange={this.handleChange} rows={4} placeholder="Please input your study methods" />,
            )}
          </FormItem>
          <div style={{ whiteSpace: 'normal' }}>
            <FormItem

              label="Expected Outcomes and Use Of Results"

              {...formItemLayout}
            >
              {getFieldDecorator('expectedOutcomes', {
                initialValue: this.state.expectedOutcomes,
                rules: [{
                  required: true,
                  message: Message.ValidationMessages.expectedOutcomeRequiredValidation,
                }, {
                  max: 200, message: Message.ValidationMessages.expectedOutcomeMaxValidation,
                }, {
                  pattern: new RegExp('^[a-zA-Z0-9-,]+(\s{0,1}[a-zA-Z0-9-, ])*$'),
                  message: Message.ValidationMessages.alphabetNumberPatternValidation,
                  }],
              })(
                <TextArea onChange={this.handleChange} name="expectedOutcomes" setfieldsvalue={this.state.expectedOutcomes} onChange={this.handleChange} rows={2} placeholder="Please input your expected outcome and use of results" />,
              )}
            </FormItem>
          </div>

          <FormItem
            label="Keywords"
            {...formItemLayout}
          >
            {getFieldDecorator('keywords', {
              initialValue: this.state.keywords,
              rules: [{
                required: true,
                message: Message.ValidationMessages.keywordRequiredValidation,
              }, {
                max: 100, message: Message.ValidationMessages.keywordMaxValidation,
              }, {
                pattern: new RegExp('^[a-zA-Z0-9-,]+(\s{0,1}[a-zA-Z0-9-, ])*$'),
                message: Message.ValidationMessages.alphabetNumberPatternValidation,
                }],
            })(
              <Input onChange={this.handleChange} name="keywords" setfieldsvalue={this.state.keywords} placeholder="Please input your keywords" />,
            )}
          </FormItem>

          <FormItem
            wrapperCol={{ xs: 24, sm: { span: 12, offset: 5 } }}
          >
            <Button type="primary" disabled={this.state.disabledButton} htmlType="submit">
              Save and Continue
            </Button>
          </FormItem>
        </Form>
      </div>

    );
  }
}


