handleInputConfirm = () => {
  const { inputValue } = this.state;
  let { tags } = this.state;
  if (inputValue && tags.indexOf(inputValue) === -1) {
    tags = [...tags, inputValue];
  }
  this.setState({
    tags,
    inputVisible: false,
    inputValue: '',
  });
};

handleInputConfirmForPrimarySupervisor = () => {
