  beforeUpload(file) {
    console.log('in before upload', file);
    const isLt2M = file.size / 1024 / 1024 < 2;

   const isJpgOrPng = file.type === 'image/jpeg' || file.type === 'image/png';
   if (!isJpgOrPng) {
     toast.error('You can only upload JPG/PNG file!', {
       position: 'top-center',
       autoClose: 5000,
       hideProgressBar: true,
       closeOnClick: true,
       pauseOnHover: true,
       draggable: false,
     });
   }
   if (isJpgOrPng) {
   if (!isLt2M) {
     toast.error('Image must smaller than 2MB!', {
       position: 'top-center',
       autoClose: 5000,
       hideProgressBar: true,
       closeOnClick: true,
       pauseOnHover: true,
       draggable: false,
     });
   }
  }
   return isJpgOrPng && isLt2M;
 }


