import {
 Card, Tabs, Modal, message,
 } from 'antd';
import React, { Component } from 'react';
import '../../css/custom.css';
import { toast } from 'react-toastify';
import URL from '../../env/environmentVar';
// eslint-disable-next-line import/no-cycle
import Tab7 from './Tab7';
import BreadcrumbDemo from '../BreadcrumbDemo';
import Tab1 from './Tab1';
import Tab2 from './Tab2';
import Tab3 from './Tab3';
import Tab4 from './Tab4';
import Tab5 from './Tab5';
import Tab6 from './Tab6';
import AllMessage from '../AllMessages';
import 'react-toastify/dist/ReactToastify.css';

const { error } = Modal;
let that;
const { TabPane } = Tabs;

class EditProposal extends Component {
  static callback(key, status) {
    that.setState({
      key,
    });
    if (status.tab2 === true) {
      that.setState({
        tab2: true,
      });
    }
    if (status.tab3 === true) {
      that.setState({
        tab3: true,
      });
    }
    if (status.tab4 === true) {
      that.setState({
        tab4: true,
      });
    }
    if (status.tab5 === true) {
      that.setState({
        tab5: true,
      });
    }
    if (status.tab6 === true) {
      that.setState({
        tab6: true,
      });
    }
  }

  constructor(props) {
    super(props);

    this.state = {
      key: '1',
      proposalId: '',
      tab2: false,
      tab3: false,
      tab4: false,
      tab5: false,
      tab6: false,
      tagState: [],

    };
    that = this;
    this.handleTabChange = this.handleTabChange.bind(this);
    this.getData = this.getData.bind(this);
    this.getTagStatus = this.getTagStatus.bind(this);
  }

componentDidMount() {
  const urlParams = new URLSearchParams(window.location.search);

    const id = urlParams.get('id');

    fetch(`${URL.APIURL}/getProposal`, {
      // mode :'no-cors',
      method: 'POST',
      headers: {
        Accept: 'application/json',
        'Content-Type': 'application/json',
        'Access-Control-Allow-Origin': '*',
      },
      credentials: 'include',
      // proposalId: this.state.id,
      body: JSON.stringify({
        proposalId: id,
      }),
    })
      .then((res) => { return res.json(); })
      .then(
        (res) => {
          if (res.status === 200) {
            console.log('^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^6', res.proposalData);
            this.setState({
              tagState: res.proposalData.coinvestigator,
            });
          if (res.proposalData.title
            && res.proposalData.background
            && res.proposalData.objectives
            && res.proposalData.studyMethods
            && res.proposalData.expectedOutcomes
            && res.proposalData.keywords) {
            this.setState({
              tab2: true,
            });
          }
          if (res.proposalData.studentResearch !== undefined
            && res.proposalData.startDate
            && res.proposalData.endDate
            && res.proposalData.keyInstitution
            && res.proposalData.multiCountryResearch !== undefined
            && res.proposalData.nationWideResearch !== undefined
            && res.proposalData.researchDomain
            && res.proposalData.researchField
            && res.proposalData.otherResearchForm
            && res.proposalData.humanSubjects !== undefined
            && res.proposalData.dataCollection
            && res.proposalData.otherCommitte !== undefined) {
            this.setState({
              tab3: true,
            });
          }
          if (res.proposalData.source && res.proposalData.amount) {
            this.setState({
              tab4: true,
            });
          }
          if (
            res.proposalData.identity !== undefined
            && res.proposalData.age !== undefined
             && res.proposalData.dependentRelation !== undefined
         && res.proposalData.minorityGroup !== undefined
          && res.proposalData.mentalImpairment !== undefined
             && res.proposalData.pregnant !== undefined
            && res.proposalData.anyTest !== undefined
         && res.proposalData.biologicalSamples !== undefined
            && res.proposalData.ionizationRadiation !== undefined
           && res.proposalData.distress !== undefined
             && res.proposalData.inducements !== undefined
          && res.proposalData.sensitiveInfo !== undefined
            && res.proposalData.reproductiveTech !== undefined
          && res.proposalData.humanGenetic !== undefined
             && res.proposalData.stemCell !== undefined
            && res.proposalData.biosafeIssue !== undefined
          ) {
            this.setState({
              tab5: true,
            });
          }
          if (res.proposalData.levelRisk
             && (res.proposalData.riskApply
            || res.proposalData.briefRisk
             || res.proposalData.managedRisk)
             && res.proposalData.benefits
             && res.proposalData.multiInistitution !== undefined
            && res.proposalData.conflict !== undefined) {
              this.setState({
                tab6: true,
              });
            }
        } else if (res.status === 300) {
          toast.error(res.message, {
            position: 'top-center',
            autoClose: 5000,
            hideProgressBar: true,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: false,
            });
} else if (res.status === 401) {
          error({
            title: AllMessage.SessionExpired + res.message,
            content: '',
            onOk() {
              that.props.history.push('/signIn');
            },

          });
        }
        },
      );
}


  getData(val) {
    // do not forget to bind getData in constructor
    this.setState({
      proposalId: val,
    });
  }

  getTagStatus(a) {
    this.setState({
      tagState: a,
    }, () => {
    });
  }

  handleTabChange(key) {
    if (this.state.key === '1') {
      that.setState({
        key,
      });
    } else if (confirm('Are you sure you want to leave this page ?')) {
      that.setState({
        key,
      });
    } else {
      that.setState({
        key: that.state.key,
      });
    }
  }

  render() {
    document.title = `Edit Proposal | ${AllMessage.PageTitle}`;
    return (
      <div>
        <BreadcrumbDemo propertyProps={this.props} />

        <Card className="cardcss" style={{ marginBottom: '22px !important' }} title={<b>Edit Proposal</b>}>

          <Tabs activeKey={that.state.key} onTabClick={this.handleTabChange}>
            <TabPane tab={<b>Submission</b>} key="1"><Tab1 /></TabPane>
            <TabPane tab={<b>Titles and abstract</b>} key="2"><Tab2 sendData={this.getData} proposalId={this.state.proposalId} /></TabPane>
            <TabPane tab={<b>Proposal details</b>} key="3"><Tab3 tagStatus={this.getTagStatus} sendData={this.getData} proposalId={this.state.proposalId} /></TabPane>
            <TabPane tab={<b>Sources of monetary or material support</b>} key="4"><Tab4 sendData={this.getData} proposalId={this.state.proposalId} /></TabPane>
            <TabPane tab={<b>Risk assessment</b>} key="5"><Tab5 sendData={this.getData} proposalId={this.state.proposalId} /></TabPane>
            <TabPane tab={<b>Potential risks and benefits</b>} key="6"><Tab6 sendData={this.getData} proposalId={this.state.proposalId} /></TabPane>
            <TabPane tab={<b>Uploading supplementary files</b>} key="7">
              {' '}
              <Tab7
                sendData={this.getData}
                Tab7Props={this.props}
                proposalId={this.state.proposalId}
                tabsStatus={this.state}
              />
            </TabPane>
          </Tabs>

        </Card>
      </div>

    );
  }
}


export default EditProposal;

import React from 'react';
import {
  Button, Form, Input, Icon, Modal,
} from 'antd';
import {
  Link,
} from 'react-router-dom';
import { toast } from 'react-toastify';
import URL from '../env/environmentVar';
import 'react-toastify/dist/ReactToastify.css';
import AllMessage from './AllMessages';

import Message from '../../../config/ValidationMessages';

const { error } = Modal;

const FormItem = Form.Item;
let that;
class ForgotPassword extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
    };
    that = this;
  }


  handleSubmit = (e) => {
    e.preventDefault();


    this.props.form.validateFields((err, values) => {
      values.email = values.email.toLowerCase();
      if (!err) {
        fetch(`${URL.APIURL}/generateForgotPasswordLink`, {
          // mode :'no-cors',
          method: 'POST',
          headers: {
            Accept: 'application/json',
            'Content-Type': 'application/json',
            'Access-Control-Allow-Origin': '*',
          },

          credentials: 'include',
          body: JSON.stringify({
            email: values.email,
          }),
        })
        .then((res) => { return res.json(); })
        .then(
          (res) => {
              if (res.status === 200) {
                this.props.history.push('/signIn');
                toast.success(res.message, {
                    position: 'top-center',
                    autoClose: 5000,
                    hideProgressBar: true,
                    closeOnClick: true,
                    pauseOnHover: true,
                    draggable: false,
                    });
              } else if (res.status === 300) {
                toast.error(res.message, {
                  position: 'top-center',
                  autoClose: 5000,
                  hideProgressBar: true,
                  closeOnClick: true,
                  pauseOnHover: true,
                  draggable: false,
                  });
              } else if (res.status === 500) {
                this.props.history.push('/serverError');
              } else if (res.status === 400) {
                // bad request
                this.props.history.push('/badRequest');
              } else if (res.status === 403) {
                // forbidden request
                this.props.history.push('/forbiddenRequest');
              }
            },
          );
      }
    });
  };


  render() {
    document.title = `Forgot Password | ${AllMessage.PageTitle}`;

    const { getFieldDecorator } = this.props.form;
    const styles = {
      paperContainer: {
        backgroundSize: 'cover',

        resizeMode: 'repeat',
        backgroundImage: `url(${'https://cdn.sqoolz.com/ui/images/banner/sqoolz-index-banner-bg.jpg'})`,

      },
  };


    return (
