import Message from '../../../config/ValidationMessages';

const { error } = Modal;
let that;
let user;
const FormItem = Form.Item;

function getBase64(img, callback) {
  const reader = new FileReader();
  reader.addEventListener('load', () => { return callback(reader.result); });
  reader.readAsDataURL(img);
}


function beforeUpload(file) {
  const isJPG = file.type === 'image/jpeg' || file.type === 'image/png';

  if (!isJPG) {
    message.error('You can only upload PNG file!');
  }
  const isLt2M = file.size / 1024 / 1024 < 2;
  if (!isLt2M) {
    message.error('Image must smaller than 2MB!');
  }
  return isJPG && isLt2M;
}

const sourceDataForDropDown = SourceData;

class MyProfile extends Component {
  constructor(props) {
    super(props);

    this.state = {
      imageUrl: '',
      loading: false,
      serverSideValidationError: [],

      disabledButton: false,
    };
    user = this.props.authUser;
    that = this;
    this.handleSubmit = this.handleSubmit.bind(this);
  }


  componentDidMount() {
    this.setState({
      user: this.props.authUser,
    });
    fetch(`${URL.APIURL}/getInternalUser`, {
      // mode :'no-cors',
      method: 'POST',
      headers: {
        Accept: 'application/json',
        'Content-Type': 'application/json',
        'Access-Control-Allow-Origin': '*',
      },
      credentials: 'include',
      body: JSON.stringify({
        internalUserId: user.id,
      }),

    })
      .then((res) => { return res.json(); })
      .then(
        (res) => {
          if (res.status === 200) {
            console.log('in component did mount 200', res);
            this.setState({
              salutation: res.internalUserData.salutation,
              firstName: res.internalUserData.firstName,
              middleName: res.internalUserData.middleName,
              lastName: res.internalUserData.lastName,
              email: res.internalUserData.email,
              jobTitle: res.internalUserData.jobTitle,
              highestQualification: res.internalUserData.highestQualification,
              department: res.internalUserData.department,
              organization: res.internalUserData.organization,
              principleField: res.internalUserData.principleField,
              nationality: res.internalUserData.nationality,
              targetArticles: res.internalUserData.targetArticles,


            }, () => {

            });
            if (user.role === 5) {
              this.setState({
                imageUrl: res.internalUserData.chairSign,

              });
            }
          } else if (res.status === 300) {
            toast.error(res.message, {
              position: 'top-center',
              autoClose: 5000,
              hideProgressBar: true,
              closeOnClick: true,
              pauseOnHover: true,
              draggable: false,
              });
          } else if (res.status === 401) {
            error({
              title: AllMessage.SessionExpired + res.message,
              content: '',
              onOk() {
                that.props.history.push('/signIn');
              },
            });
          } else if (res.status === 500) {
          this.props.history.push('/serverError');
        } else if (res.status === 400) {
          // bad request
          this.props.history.push('/badRequest');
        } else if (res.status === 403) {
          // forbidden request
          this.props.history.push('/forbiddenRequest');
        }
        },


      );
  }


  handleChange = (info) => {
console.log('(info.file.status dfgdfg', info);

      console.log('(info.file.status dfgdfg', info.file);
          if (info.file.status === 'uploading') {
      this.setState({ loading: true });
      return;
    }
    if (info.file.status === 'done') {
      console.log('(info.file.status dfgdfg', info);

      console.log('(info.file.status dfgdfg', info.file);

      // Get this url from response in real world.
      getBase64(info.file.originFileObj, (imageUrl) => {
return this.setState({
        imageUrl,
        loading: false,
      }, () => {
        console.log('imageUrl', this.state.imageUrl);
      });
});
    }
  };

  handleSubmit = (e) => {
    this.setState({
      disabledButton: false,
    });
    e.preventDefault();
    this.props.form.validateFields((err, values) => {
      if (!err) {
        values.id = user.id;
        values.isProfileComplete = true;
        values.chairSign = this.state.imageUrl;
        console.log('values in hanfdlr submit', values);
        this.setState({
          disabledButton: true,
        });
        fetch(`${URL.APIURL}/updateInternalUser`, {
          // mode :'no-cors',
          method: 'POST',
          headers: {
            Accept: 'application/json',
            'Content-Type': 'application/json',
            'Access-Control-Allow-Origin': '*',
          },
          credentials: 'include',
          body: JSON.stringify({
            data: values,
          }),
        })
          .then((res) => { return res.json(); })
          .then(
            (res) => {
              if (res.status === 200) {
                toast.success(res.message, {
                  position: 'top-center',
                  autoClose: 5000,
                  hideProgressBar: true,
                  closeOnClick: true,
                  pauseOnHover: true,
                  draggable: false,
                  });
                this.setState({
                  disabledButton: false,
                  serverSideValidationError: [],
                });

                window.location.reload();
              } else if (res.status === 300) {
                this.setState({
                  disabledButton: false,
                  serverSideValidationError: [],
                });
                toast.error(res.message, {
                  position: 'top-center',
                  autoClose: 5000,
                  hideProgressBar: true,
                  closeOnClick: true,
                  pauseOnHover: true,
                  draggable: false,
                  });
              } else if (res.status === 422) {
                this.setState({
                  disabledButton: false,
                  serverSideValidationError: res.message,
                });
              } else if (res.status === 500) {
                this.setState({
                  disabledButton: false,
                });
                this.props.history.push('/serverError');
              } else if (res.status === 400) {
                this.setState({
                  disabledButton: false,
                });
                // bad request
                this.props.history.push('/badRequest');
              } else if (res.status === 403) {
                this.setState({
                  disabledButton: false,
                });
                // forbidden request
                this.props.history.push('/forbiddenRequest');
              }
            },


          );
      }
    });
  }


  render() {
    document.title = `Profile | ${AllMessage.PageTitle}`;
    const uploadButton = (
      <div>
        <Icon type={this.state.loading ? 'loading' : 'plus'} />
        <div className="ant-upload-text">Upload</div>
      </div>
    );
    const { getFieldDecorator } = this.props.form;

    const formItemLayout = {
      labelCol: {
        xs: { span: 24 },
        sm: { span: 8 },
      },
      wrapperCol: {
        xs: { span: 24 },
        sm: { span: 16 },
      },
    };


    const { imageUrl } = this.state;

    return (
      <div>
        <BreadcrumbDemo />

        <Card className="cardcss" title={<b>Profile</b>}>
          <Form onSubmit={this.handleSubmit}>


            <FormItem
              label="Salutation"
              {...formItemLayout}
            >
              {getFieldDecorator('salutation', {
              initialValue: this.state.salutation,

              rules: [{
                required: true,
                 message: Message.ValidationMessages.salutationRequiredMessage,
            }],
            })(
              <Select

                placeholder="Please select salutation"
                onChange={this.handleSelectChange}
              >
                <Select.Option value="Dr">Dr</Select.Option>
                <Select.Option value="Mr">Mr</Select.Option>
                <Select.Option value="Mrs">Mrs</Select.Option>
                <Select.Option value="Miss">Miss</Select.Option>
                <Select.Option value="Sir">Sir</Select.Option>
