    } else if (layoutType === LAYOUT_TYPE_BOXED) {
      document.body.classList.remove("full-layout");
      document.body.classList.remove("framed-layout");
      document.body.classList.add("boxed-layout");
    } else if (layoutType === LAYOUT_TYPE_FRAMED) {
      document.body.classList.remove("boxed-layout");
      document.body.classList.remove("full-layout");
      document.body.classList.add("framed-layout");
    }
  };

  setNavStyle = (navStyle) => {
    if (
      navStyle === NAV_STYLE_DEFAULT_HORIZONTAL ||
      navStyle === NAV_STYLE_DARK_HORIZONTAL ||
      navStyle === NAV_STYLE_INSIDE_HEADER_HORIZONTAL ||
      navStyle === NAV_STYLE_ABOVE_HEADER ||
      navStyle === NAV_STYLE_BELOW_HEADER
    ) {
      document.body.classList.add("full-scroll");
      document.body.classList.add("horizontal-layout");
    } else {
      document.body.classList.remove("full-scroll");
      document.body.classList.remove("horizontal-layout");
    }
  };

  componentWillMount() {
    fetch(`${URL.APIURL}/isAuthenticated`, {
      // mode :'no-cors',
      method: "POST",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
        "Access-Control-Allow-Origin": "*",
      },
      credentials: "include",
    })
      .then((res) => {
        return res.json();
      })
      .then((res) => {
        console.log("res....in is authenticated", res);
        if (res.status === 200) {
          this.props.userSignIn(res.userData);
          global.user = res.userData;
          this.setState({
            isLoading: false,
          });
          if (
            window.location.pathname === "/signIn" &&
            global.user !== "" &&
            global.user !== undefined
          ) {
            this.props.history.push("/profile");
          }
        } else if (res.status === 300 && res.message === "Please login") {
          global.user = "";
          this.setState({
            isLoading: false,
          });
          if (
            window.location.pathname === "/confirmAccount" ||
            window.location.pathname === "/confirmInvestigatorAccount" ||
            window.location.pathname === "/forgotPassword"
          ) {
          } else {
            that.props.history.push("/signIn");
          }
        }
      });

    if (this.props.initURL === "") {
      this.props.setInitUrl(this.props.history.location.pathname);
    }
    const params = new URLSearchParams(this.props.location.search);

    if (params.has("theme")) {
      this.props.setThemeType(params.get("theme"));
    }
    if (params.has("nav-style")) {
      this.props.onNavStyleChange(params.get("nav-style"));
    }
    if (params.has("layout-type")) {
      this.props.onLayoutTypeChanewProposalnge(params.get("layout-type"));
    }
  }

  render() {
    const {
      match,
      location,
      themeType,
      layoutType,
      navStyle,
      locale,
      authUser,
      initURL,
    } = this.props;
    console.log("this.props.authuserrrrrrrrrrrrrrrrrrrr", authUser);
    const RestrictedRoute = ({ component: Component, authUser, ...rest }) => {
      return this.state.isLoading === true && global.user === undefined ? (
        <Spin style={{ marginLeft: "50%", marginTop: "20%" }} size="large" />
      ) : (
        <Route
          {...rest}
          render={(props) => {
            return that.props.authUser ? (
              <Component {...props} />
            ) : (
              <Redirect
                to={{
                  pathname: "/signIn",
                  state: { from: props.location },
                }}
              />
            );
          }}
        />
      );
    };
    if (themeType === THEME_TYPE_DARK) {
      document.body.classList.add("dark-theme");
    }
    if (location.pathname === "/") {
      return <Redirect to="/signIn" />;
    }
    this.setLayoutType(layoutType);

    this.setNavStyle(navStyle);

    return (
      <LocaleProvider>
        <IntlProvider>
          <Switch>
            <Route exact path="/signIn" component={SignIn} />
            <Route exact path="/signUp" component={SignUp} />
            <Route exact path="/confirmAccount" component={ConfirmAccount} />
            <Route exact path="/forgotPassword" component={ForgotPassword} />
            <Route exact path="/resetPassword" component={ResetPassword} />
            <Route
              exact
              path="/confirmInvestigatorAccount"
              component={ConfirmInvestigatorAccount}
            />
            {/* {!user? <Route component={Page404} />:null} */}
            {/* <Route path={`${match.url}`}
            component={MainApp} /> */}
            <RestrictedRoute
              //  path={`${match.url}`}
              //  user={user}
              component={MainApp}
            />
          </Switch>
        </IntlProvider>

        <ToastContainer />
      </LocaleProvider>
    );
  }
}

