import React, { Component } from 'react';
import { connect } from 'react-redux';
import { Drawer, Layout } from 'antd';

import SidebarContent from './SidebarContent';
import { toggleCollapsedSideNav, updateWindowWidth } from '../../appRedux/actions/Setting';
import {
  NAV_STYLE_DRAWER,
  NAV_STYLE_FIXED,
  NAV_STYLE_MINI_SIDEBAR,
  NAV_STYLE_NO_HEADER_EXPANDED_SIDEBAR,
  NAV_STYLE_NO_HEADER_MINI_SIDEBAR,
  TAB_SIZE,
  THEME_TYPE_LITE,
} from '../../constants/ThemeSetting';
import SideBarContentInvestigator from './SideBarContentInvestigator';

const { Sider } = Layout;

export class Sidebar extends Component {
  onToggleCollapsedNav = () => {
    this.props.toggleCollapsedSideNav(!this.props.navCollapsed);
  };

  componentDidMount() {
    window.addEventListener('resize', () => {
      this.props.updateWindowWidth(window.innerWidth);
    });
  }

  render() {
    const {
 themeType, navCollapsed, width, navStyle,
} = this.props;
    let drawerStyle = 'gx-collapsed-sidebar';

    if (navStyle === NAV_STYLE_FIXED) {
      drawerStyle = '';
    } else if (navStyle === NAV_STYLE_NO_HEADER_MINI_SIDEBAR) {
      drawerStyle = 'gx-mini-sidebar gx-mini-custom-sidebar';
    } else if (navStyle === NAV_STYLE_NO_HEADER_EXPANDED_SIDEBAR) {
      drawerStyle = 'gx-custom-sidebar';
    } else if (navStyle === NAV_STYLE_MINI_SIDEBAR) {
      drawerStyle = 'gx-mini-sidebar';
    } else if (navStyle === NAV_STYLE_DRAWER) {
      drawerStyle = 'gx-collapsed-sidebar';
    }
    if ((navStyle === NAV_STYLE_FIXED || navStyle === NAV_STYLE_MINI_SIDEBAR
      || navStyle === NAV_STYLE_NO_HEADER_EXPANDED_SIDEBAR) && width < TAB_SIZE) {
      drawerStyle = 'gx-collapsed-sidebar';
    }
    return (
      <Sider
        className={`gx-app-sidebar ${drawerStyle} ${themeType !== THEME_TYPE_LITE ? 'gx-layout-sider-dark' : null}`}
        trigger={null}
        collapsed={(width < TAB_SIZE ? false : navStyle === NAV_STYLE_MINI_SIDEBAR || navStyle === NAV_STYLE_NO_HEADER_MINI_SIDEBAR)}
        theme={themeType === THEME_TYPE_LITE ? 'lite' : 'dark'}
        collapsible
      >
        {
          navStyle === NAV_STYLE_DRAWER || width < TAB_SIZE
            ? (
              <Drawer
                wrapClassName={`gx-drawer-sidebar ${themeType !== THEME_TYPE_LITE ? 'gx-drawer-sidebar-dark' : null}`}
                placement="left"
                closable={false}
                onClose={this.onToggleCollapsedNav.bind(this)}
                visible={navCollapsed}
              >

                <SidebarContent widthProps={width} />
              </Drawer>
)

                  : <SidebarContent widthProps={width} />


        }
      </Sider>
);
  }
}

const mapStateToProps = ({ settings }) => {
  const {
 themeType, navStyle, navCollapsed, width, locale,
} = settings;
  return {
 themeType, navStyle, navCollapsed, width, locale,
};
};
export default connect(mapStateToProps, { toggleCollapsedSideNav, updateWindowWidth })(Sidebar);

import React from 'react';
import {
  Button, Form, Input, Icon,
} from 'antd';
import { connect } from 'react-redux';
import {
  Link,
} from 'react-router-dom';
import { toast } from 'react-toastify';
import URL from '../env/environmentVar';
import 'react-toastify/dist/ReactToastify.css';
import AllMessage from '../MyComponents/AllMessages';
import Message from '../../../config/ValidationMessages';
import {
  userSignIn,
} from '../appRedux/actions/Auth';

const FormItem = Form.Item;

class SignIn extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      data: '',
    };
  }


  handleSubmit = (e) => {
    e.preventDefault();

    // const form = this.props;
    // eslint-disable-next-line react/prop-types
    this.props.form.validateFields((err, values) => {
      if (!err) {
        values.email = values.email.toLowerCase().trim();
        fetch(`${URL.APIURL}/processLogin`, {
          // mode :'no-cors',
          method: 'POST',
          headers: {
            Accept: 'application/json',
            'Content-Type': 'application/json',
            'Access-Control-Allow-Origin': '*',
          },

          credentials: 'include',
          body: JSON.stringify({
            username: values.email,
            password: values.password,
          }),
        })
        .then((res) => { return res.json(); })
        .then(
          (res) => {
              if (res.status === 200) {
                this.props.userSignIn(res.user);
                global.user = res.user;
                this.setState({
                  data: res,
                });

                if (this.state.data.user.role === 1) {
                      this.props.history.push('/profile');
                } else if (this.state.data.user.role === 6) {
                    this.props.history.push('/profile');
                } else if (this.state.data.user.role === 2) {
                      this.props.history.push('/profile');
                } else if (this.state.data.user.role === 3) {
                      this.props.history.push('/profile');
                } else if (this.state.data.user.role === 5) {
                      this.props.history.push('/profile');
