          { userData.role === 6 && <Route exact path="/acceptanceLetter" component={ResearchPdfInvestigator} /> }
          { userData.role === 6 && <Route exact path="/searchContent" component={SearchContentExceptOfCoordinator} /> }
          { userData.role === 5 && <Route exact path="/searchContent" component={SearchContentExceptOfCoordinator} /> }

          { userData.role === 2 && <Route exact path="/manageProposal" component={ManageProposal} /> }
          { userData.role === 2 && <Route exact path="/managePolicy" component={ManagePolicy} /> }
          { userData.role === 2 && <Route exact path="/managePolicy/addPolicy" component={AddPolicy} /> }
          { userData.role === 2 && <Route exact path="/userManagement" component={UserManagementCoordinator} /> }
          { userData.role === 2 && <Route exact path="/searchContent" component={SearchContentCoordinator} /> }
          { userData.role === 2 && <Route exact path="/manageCommittee" component={ManageCommitteeCoordinator} /> }
          { userData.role === 2 && <Route exact path="/addMember" component={AddMember} /> }
          { userData.role === 2 && <Route exact path="/dashboard" component={DashboardCoordinator} /> }
          { userData.role === 2 && <Route exact path="/announcemnet" component={AnnouncementCoordinator} /> }


          { userData.role === 2 && <Route exact path="/ViewCommitteeMember" component={ViewCommitteeMember} /> }
          { userData.role === 2 && <Route exact path="/manageResearch" component={ManageResearchCoordinator} /> }
          { userData.role === 2 && <Route exact path="/acceptanceLetter" component={PdfViewCoordinator} /> }

          { userData.role === 3 && <Route exact path="/dashboard" component={DashboardReviewer} /> }
          { userData.role === 3 && <Route exact path="/reviewProposal" component={ReviewProposalReviewer} /> }
          { userData.role === 3 && <Route exact path="/searchContent" component={SearchContentReviewer} /> }

          { userData.role === 4 && <Route exact path="/dashboard" component={DashboardReviewer} /> }
          { userData.role === 4 && <Route exact path="/reviewProposal" component={ReviewProposalReviewer} /> }
          { userData.role === 4 && <Route exact path="/searchContent" component={SearchContentReviewer} /> }

          { userData.role === 5 && <Route exact path="/dashboard" component={DashboardChair} /> }
          { userData.role === 5 && <Route exact path="/signProposal" component={SignContent} /> }
          { userData.role === 5 && <Route exact path="/viewProposal" component={ViewProposalChair} /> }
          { userData.role === 5 && <Route exact path="/researchPdf" component={ProposalToResearchPdf} /> }
          { userData.role === 5 && <Route exact path="/pdfView" component={pdfView} /> }

          { userData.role === 1 && <Route exact path="/dashboard" component={DashboardAdmin} /> }
          { userData.role === 1 && <Route exact path="/userManagement" component={UserManagementAdmin} /> }
          { userData.role === 1 && <Route exact path="/managePolicy" component={ManagePolicyAdmin} /> }
          { userData.role === 1 && <Route exact path="/announcement" component={AnnouncementAdmin} /> }

