  uploadApplicationDocument(req, res) {
    Logger.debug('ApplicationController.uploadApplicationDocument');
    const proposalId = req.body.proposalId;
    if (!proposalId) {
      Logger.warn('Proposal id not found');
      res.send({ status: 300, message: sails.config.serverMessage.serverError });
    } else {
      req.file('file').upload({
        dirname: path.resolve(sails.config.appPath, `assets/ApplicationDocuments/${proposalId}`),
        saveAs: req.body.fileName,
      }, (uploadErr, files) => {
        if (uploadErr) {
          Logger.error(`ApplicationController.uploadApplicationDocument at req.file ${uploadErr}`);
          res.send({ status: 300, message: sails.config.serverMessage.serverError });
        } else if (files.length === 0) {
          Logger.warn('Document not found');
          res.send({ status: 300, message: 'Document not found' });
        } else {
          const file = files[0];
          const fileLocArray = file ? file.fd.split('/') : [];
          const fileLoc = fileLocArray[fileLocArray.length - 1];
          const uploadFile = {
            uid: fileLoc,
            size: file.size,
            type: file.type,
            name: file.filename,
            url: `/ApplicationDocuments/${proposalId}/${fileLoc}`,
            status: 'done',
          };
          Logger.verbose(uploadFile);
          const proposalData = {};
          proposalData[req.body.fileName] = uploadFile;
          ApplicationService.updateProposal(proposalId, proposalData, (err, updatedProposal) => {
            if (err) {
              res.send({ status: 300, message: sails.config.serverMessage.serverError });
            } else {
              res.send({ status: 200, message: sails.config.serverMessage.uploadFileSuccess, proposalData: updatedProposal });
            }
          });
        }
      });
    }
  },
