      {
        name: 'Biosafety issue?',
        value: proposalData.biosafeIssue,
        validations: [{ validation: 'notEmpty', message: sails.config.ValidationMessages.fieldRequiredValidation },
          { validation: 'boolean', message: sails.config.ValidationMessages.booleanValidation }],
      },


      {
        name: 'Level of the risk involved in research',
        value: proposalData.levelRisk,
        validations: [{ validation: 'notEmpty', message: sails.config.ValidationMessages.riskInvolvedValidation }],
      },
      {
        name: 'Benefits from the research project',
        value: proposalData.benefits,
        validations: [{ validation: 'notEmpty', message: sails.config.ValidationMessages.benifitsFromValidation }],
      },
      {
        name: 'Multi-institution project',
        value: proposalData.multiInistitution,
        validations: [{ validation: 'notEmpty', message: sails.config.ValidationMessages.fieldRequiredValidation },
          { validation: 'boolean', message: sails.config.ValidationMessages.booleanValidation }],
      },
      {
        name: 'Conflict of interest',
        value: proposalData.conflict,
        validations: [{ validation: 'notEmpty', message: sails.config.ValidationMessages.fieldRequiredValidation }],
      },

    ];
    ValidateService.validate(args, (validationErr, validationErrMsgs) => {
      if (validationErr) {
        Logger.error(`InternalUserController.registration at ValidateService.validate ${validationErr}`);
        res.send({ status: 300, message: sails.config.serverMessage.serverError });
      } else if (validationErrMsgs.length === 0) {
        proposalData.investigatorId = new ObjectId(req.session.user.id);
        Logger.verbose(proposalData);
        if (proposalData.proposalId) {
          const proposalId = proposalData.proposalId;
          ApplicationService.updateProposal(proposalId, proposalData, (err, updatedProposal) => {
            if (err) {
              Logger.error(`ApplicationController.submitProposal at ApplicationService.updateProposal ${err}`);
              res.send({ status: 300, message: sails.config.serverMessage.serverError });
            } else {
              Logger.verbose(updatedProposal);
              const proposal = updatedProposal[0];
              Logger.verbose(proposal);
              if (proposal) {
                ApplicationService.updateProposal(proposalId, { isSubmitted: true }, (updateProposalErr, updatedProposalData) => {
                  if (updateProposalErr) {
                    Logger.error(`ApplicationController.submitProposal at ApplicationService.updateProposal ${updateProposalErr}`);
                    res.send({ status: 300, message: sails.config.serverMessage.serverError });
                  } else {
                    EmailService.submitPropsalEmailToInvestigator(req.session.user, updatedProposalData[0]);
                    res.send({ status: 200, message: sails.config.serverMessage.proposalSavedSuccess, proposalData: updatedProposalData });
                  }
                });
              } else {
                Logger.error('ApplicationController.submitProposal at ApplicationService.updateProposal');
                res.send({ status: 300, message: sails.config.serverMessage.serverError });
              }
            }
          });
        } else {
          res.send({ status: 300, message: sails.config.serverMessage.serverError });
        }
      } else {
        Logger.verbose('validation error');
        Logger.verbose({ status: 422, validationErrArr: validationErrMsgs });
        res.send({ status: 422, validationErrArr: validationErrMsgs });
      }
    });
  },

