  assignReviewType(req, res) {
    Logger.debug('ApplicationController.assignReviewType');
    const proposalId = req.body.proposalId;
    // const reviewType = req.body.reviewType;
    let reviewType;

    if(req.body.reviewType==='Exempted_Review'){
      reviewType = sails.config.reviewTypeMapping.Exempted_Review
    }else if(req.body.reviewType==='Expedited_Review'){
      reviewType = sails.config.reviewTypeMapping.Expedited_Review
    }
    else{
      reviewType = sails.config.reviewTypeMapping.Full_Review
    }

    Logger.verbose(proposalId);
    Logger.verbose(reviewType);

    const args = [
      {
        name: 'proposalId',
        value: proposalId,
        validations: [{ validation: 'notEmpty', message: sails.config.ValidationMessages.fieldRequiredValidation }],
      },
      {
        name: 'reviewType',
        value: reviewType,
        validations: [
          { validation: 'notEmpty', message: sails.config.ValidationMessages.fieldRequiredValidation },
          { validation: 'range', min: 0, max: 2, message: sails.config.ValidationMessages.reviewTypeValidation }]
      },
    ]
    ValidateService.validate(args, (validationErr, validationErrMsgs) => {
      if (validationErr) {
        Logger.error(`ApplicationController.assignReviewType at ValidateService.validate ${validationErr}`);
        res.send({ status: 300, message: sails.config.serverMessage.serverError });
      }
      else if(validationErrMsgs.length === 0){
        console.log('Inside else if of validation service');
        ApplicationService.updateProposal(proposalId, { reviewType, isUnderReview: true}, (err, updatedProposal) => {
          if (err) {
            res.send({ status: 300, message: sails.config.serverMessage.serverError });
          } else {
            console.log('updated proposal data', updatedProposal)
            res.send({ status: 200, proposalData: updatedProposal, message: sails.config.serverMessage.proposalAssigned });
          }
        });
      }
      else {
        console.log('inside else part')
        ValidateService.getValidateMsg(validationErrMsgs, (getValidateMsgErr, errMsgs) => {
          console.log('validationErrMsgs', validationErrMsgs)
          console.log('getValidateMsgErr', getValidateMsgErr)
          if (getValidateMsgErr) {
            Logger.error(`ApplicationController.assignReviewType at ValidateService.getValidateMsg ${getValidateMsgErr}`);
            res.send({ status: 300, message: sails.config.serverMessage.serverError });
          } else {
            // Logger.verbose(errMsgs);
            Logger.error(errMsgs[0]);
            console.log('errMsgs', errMsgs[0])
            res.send({ status: 422, message: errMsgs[0] });
          }
        });
      }
    });
  },

