        Logger.warn('proposal id not present.');
        callback('Not Present');
      }
    });
  },
};

module.exports = {

  tableName: 'fijihrp_announcement',

  attributes: {
    isActive: {
      type: 'boolean',
      defaultsTo: true,
    },
    title: {
      type: 'string',
      defaultsTo: '',
    },
    content: {
      type: 'string',
      defaultsTo: '',
    },
  },
  customToJSON() {
    return _.omit(this, ['createdAt','updatedAt','id']);
  },

  findByCriteria(criteria, callback) {
    Announcement.find(criteria).exec((err, data) => {
      callback(err, data);
    });
  },

  findActiveById(id, callback) {
    Announcement.findOne({ id, isActive: true }).exec((err, data) => {
      callback(err, data);
    });
