import React,{Component}  from 'react';
import {
  Card,Button,Input,Space,Table,Modal,Icon,Form, Tooltip
 } from 'antd';
//  import moment from 'moment';
 import { toast } from 'react-toastify';
 import URL from '../../env/environmentVar';
 import BreadcrumbDemo from '../BreadcrumbDemo';
 import AllMessage from '../AllMessages';
 import 'react-toastify/dist/ReactToastify.css';
 import { Spin } from 'antd';



const FormItem = Form.Item;

const {Search,TextArea} = Input




 export class AddAnnouncement extends Component {


  state = {

    announcementTitle:'',
    announcementDescription:'',
    disableSubmit:false

  };


  handleCancel_EditAnnouncement=()=>{

    console.log('on cancel click of edit announ');
    this.props.history.push('/manageAnnouncement')
  }

  handleSubmit_AddAnnouncement=(event)=>{
    //call api here to post announcement

  event.preventDefault()
  // this.setState({disableSubmit:true})
  this.props.form.validateFields((err, values) => {
    console.log('error********',err)
    console.log('value********',values)

    if(!err)
    {
      this.setState({disableSubmit:true})
    }
  })



fetch(`${URL.APIURL}/announcements`,{
  method: 'POST',
  headers: { 
    Accept: 'application/json',
    'Content-Type': 'application/json',
    'Access-Control-Allow-Origin': '*',
  },
  credentials: 'include',
  body: JSON.stringify(
     {
    'data':   
    {title : this.state.announcementTitle,
    content:this.state.announcementDescription}})
  })
  .then((res) => { return res.json(); })

  .then(
    (res) => {
      if (res.status === 200) {
        this.setState({
          announcementData: res.announcementData,
          disableSubmit:false
        });
        //show toaster message if status==200

          toast.success(res.message, {
          position: 'top-center',
          autoClose: 3000,
          hideProgressBar: true,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: false,
          });

      setTimeout(()=>{
        this.props.history.push('/manageAnnouncement')
      },2000)
       } else if (res.status === 300) {
         this.setState({disableSubmit:false})
        toast.error(res.message, {
          position: 'top-center',
          autoClose: 5000,
          hideProgressBar: true,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: false,
          });
      }

      else if (res.status === 401) {
        error({
          title: AllMessage.SessionExpired + res.message,
          content: '',
          onOk() {
            that.props.history.push('/signIn');
          },

        });
        this.setState({disableSubmit:false})

      } else if (res.status === 500) {
        toast.error(AllMessage.Error, {
          position: 'top-center',
          autoClose: 5000,
          hideProgressBar: true,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: false,
          });
         this.setState({disableSubmit:false})

        this.props.history.push('/serverError');
      } else if (res.status === 400) {
        toast.error(AllMessage.Error, {
          position: 'top-center',
          autoClose: 5000,
          hideProgressBar: true,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: false,
          });
        // bad request
        this.setState({disableSubmit:false})

        this.props.history.push('/badRequest');
      } else if (res.status === 403) {
        toast.error(AllMessage.Error, {
          position: 'top-center',
          autoClose: 5000,
          hideProgressBar: true,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: false,
          });
         this.setState({disableSubmit:false})

        // forbidden request
        this.props.history.push('/forbiddenRequest');
      }
    },

);
}

  handleInputChange=(event)=>{
      console.log('on change of textarea title od announce...')
      console.log(event.target.name)

      console.log(event.target.value)
      this.setState({
        [event.target.name] : event.target.value
      })
  }



  render() {
    document.title = `Add Announcement | ${AllMessage.PageTitle}`;

    const { getFieldDecorator } = this.props.form;

    const formItemLayout = {
      labelCol: {
        xs: { span: 24 },
        sm: { span: 8 },
      },
      wrapperCol: {
        xs: { span: 24 },
        sm: { span: 16 },
      },
    };
    const tailFormItemLayout = {
      wrapperCol: {
        xs: {
          span: 24,
          offset: 0,
        },
        sm: {
          span: 16,
          offset: 8,
        },
      },
    };

    return (
      <div>
        <BreadcrumbDemo propertyProps={this.props} />
        <Form onSubmit={this.handleSubmit_AddAnnouncement}>

        <Card className="cardcss" title={<b>Announcements:Add</b>}>

            <Card type="inner" title={

       <FormItem {...formItemLayout}
       label="Title : "

       >
          {getFieldDecorator('announcementTitle', {
                rules: [
                { required: true, message: AllMessage.AnnouncementTitleValidation },

              ],

            })(

         <Input name="announcementTitle"  onChange={this.handleInputChange}  setfieldsvalue={this.state.announcementTitle}/>
            )}
        </FormItem>}>

     <FormItem
      {...formItemLayout}
       label="Description : "

     >
     {getFieldDecorator('announcementDescription', {
                rules: [{ required: true, message: AllMessage.AnnouncementDescValidation },
                  {max :5000,message:AllMessage.MaxLengthDescriptionValidation}

              ],

            })(
              <TextArea name="announcementDescription"  setfieldsvalue={this.state.announcementDescription} onChange={this.handleInputChange} style={{height:200,backgroundColor:'transparent'}}/>
            )}
     </FormItem>
 <FormItem {...tailFormItemLayout}>
 <Button href='/manageAnnouncement'>Cancel</Button>

 {/* on submit pass call api to update */}
 <Button type="primary" htmlType="submit"  disabled={this.state.disableSubmit}>Submit</Button>
 </FormItem>

      </Card> 

    </Card>
    </Form>

      </div>
    );
  }
}
const AddAnnouncementC = Form.create()(AddAnnouncement);
export default AddAnnouncementC;

import React from 'react';
import {
Button, Card, Table, Checkbox, Tooltip, Icon, message, Modal,
 } from 'antd';
import { toast } from 'react-toastify';
import URL from '../../env/environmentVar';
import BreadcrumbDemo from '../BreadcrumbDemo';
import AllMessage from '../AllMessages';
import 'react-toastify/dist/ReactToastify.css';

const { error } = Modal;
let that;

const addedMember = [
  {
 title: <div style={{ fontSize: 14 }}><b>Name</b></div>, dataIndex: 'name', key: 'name', ellipsis: true,
},
  {
 title: <div style={{ fontSize: 14 }}><b>Email</b></div>, dataIndex: 'email', key: 'email', ellipsis: true,
},

  { title: <div style={{ fontSize: 14 }}><b>Action</b></div>, dataIndex: 'action', key: 'action' },
];


class AddMember extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      data: [],
      key: [],
      members: [],
      originalData: [],
      committeeMembers: [],
      committeeLength: '',
      keyLength: '',
      disabledButton: false,
    };
    that = this;
    this.handleAddCommittee = this.handleAddCommittee.bind(this);
    this.handleButtonClick = this.handleButtonClick.bind(this);
  }


  componentDidMount() {
    fetch(`${URL.APIURL}/getAllInternalReviewer`, {
        method: 'POST',
        headers: {
          Accept: 'application/json',
          'Content-Type': 'application/json',
          'Access-Control-Allow-Origin': '*',
        },
        credentials: 'include',
        body: JSON.stringify({
         }),
  })
        .then((res) => { return res.json(); })
        .then(
          (res) => {
            if (res.status === 200) {
              this.setState({
                data: res.internalReviewerData,
                originalData: res.internalReviewerData,
              });
             } else if (res.status === 300) {
              toast.error(res.message, {
                position: 'top-center',
                autoClose: 5000,
                hideProgressBar: true,
                closeOnClick: true,
                pauseOnHover: true,
                draggable: false,
                });
} else if (res.status === 401) {
              error({
                title: AllMessage.SessionExpired + res.message,
                content: '',
                onOk() {
                  that.props.history.push('/signIn');
                },

              });
            } else if (res.status === 500) {
              this.props.history.push('/serverError');
            } else if (res.status === 400) {
              // bad request
              this.props.history.push('/badRequest');
            } else if (res.status === 403) {
              // forbidden request
              this.props.history.push('/forbiddenRequest');
            }
          },

  );
    fetch(`${URL.APIURL}/getAllCommitteeMember`, {
     method: 'POST',
      headers: {
      Accept: 'application/json',
      'Content-Type': 'application/json',
      'Access-Control-Allow-Origin': '*',
    },
      credentials: 'include',
      body: JSON.stringify({
     }),
})
    .then((res) => { return res.json(); })
    .then(
      (res) => {
        if (res.status === 200) {
          this.setState({
           committeeMembers: res.committeeMemberData,
           committeeLength: res.committeeMemberData.length,
          });
         } else if (res.status === 300) {
          toast.error(res.message, {
            position: 'top-center',
            autoClose: 5000,
            hideProgressBar: true,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: false,
            });
} else if (res.status === 500) {
  this.props.history.push('/serverError');
} else if (res.status === 400) {
  // bad request
  this.props.history.push('/badRequest');
} else if (res.status === 403) {
  // forbidden request
  this.props.history.push('/forbiddenRequest');
}
      },

);
    }

  start = () => {
    setTimeout(() => {
      this.setState({
      });
    }, 1000);
  };

  handleCheckBoxClick(key1, index) {
    const { key } = this.state;

      const ref = `ref_${ index}`;


     this.refs[ref].checked = !this.refs[ref].checked;

     if (this.refs[ref].checked === false) {
       const res = key.filter((key) => {
        return !key.includes(key1);
        });
  this.setState({
    key: res,

  },
  () => {

  });
     } else if (this.refs[ref].checked === true) {
      this.setState({
        key: [...this.state.key, key1],

      },
      () => {

      });
     }
    }

  handle() {
    const columns = [
      { title: <div style={{ fontSize: 14 }}><Button onClick={this.handleButtonClick} style={{ marginBottom: '0px' }} type="primary">Add</Button></div>, dataIndex: 'add', key: 'add' },
      { title: <div style={{ fontSize: 14 }}><b>Name</b></div>, dataIndex: 'name', key: 'name' },
      { title: <div style={{ fontSize: 14 }}><b>Email</b></div>, dataIndex: 'email', key: 'email' },
      { title: <div style={{ fontSize: 14 }}><b>Expertise</b></div>, dataIndex: 'expertise', key: 'expertise' },

    ];
    return columns.map((item, index) => {
      return {
        title: item.title,
        dataIndex: item.dataIndex,
        key: item.key,
      };
    });
  }

  handleButtonClick() {
    const {
  data, key, members, committeeLength, keyLength,
  } = this.state;

     const errorLength = 10 - committeeLength - keyLength;

     const checkLimit = 10 - committeeLength - key.length;

     if (checkLimit < 0) {
       if (errorLength === 0) {
        toast.error(AllMessage.CommitteeLengthLimit, {
          position: 'top-center',
          autoClose: 5000,
          hideProgressBar: true,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: false,
          });
       } else if (errorLength !== 0) {
        toast.error(AllMessage.ErrorLengthOfCommittee1 + errorLength
          + AllMessage.ErrorLengthOfCommittee2, {
          position: 'top-center',
          autoClose: 5000,
          hideProgressBar: true,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: false,
          });
         }
     } else if (checkLimit >= 0) {
       const checkedData = data.filter((data1) => {
       return key.includes(data1.id);
       });
         members.push(checkedData);


         const removeMemberFromAllMembers = data.filter((data1) => {
         return !key.includes(data1.id);
         });

         this.setState({
             members,
             data: removeMemberFromAllMembers,

             keyLength: key.length,

       });
     }
   }


  handleRemoveClick(key) {
    const {
 members, data, originalData, keyLength,
} = this.state;

    const memberToRemove = members.flatMap((x) => { return x; });
    const removeSelectedMember = memberToRemove.filter((removeMember) => {
      return !key.includes(removeMember.id);
      });
      const addUnselectedMemberToMainTable = originalData.filter((unselectedMember) => {
        return key.includes(unselectedMember.id);
        });

        const afterDeleteKeys = keyLength - addUnselectedMemberToMainTable.length;

        data.push(addUnselectedMemberToMainTable);

        const AddMemeberConvertingArray = data.flatMap((x) => { return x; });

        this.setState({
          members: removeSelectedMember,
          data: AddMemeberConvertingArray,
           key: [],
          keyLength: afterDeleteKeys,
        });
    }

  dataFromAboveDataTable() {
    const { members } = this.state;
    const membersToAdd = members.flatMap((x) => { return x; });
    return membersToAdd.map((item, index) => {
      return {
        key: index,
        name: `${item.firstName } ${ item.lastName}`,
        email: item.email,
        status: item.status,
       action: <Tooltip placement="top" title="Remove"><Icon onClick={() => { return this.handleRemoveClick(item.id); }} style={{ marginRight: '15px', fontSize: '17px', color: 'red' }} type="close-circle" /></Tooltip>,
      };
    });
  }

  handleAddCommittee() {
    this.state.disabledButton = true;
    this.setState({
      disabledButton: true,
    });
    const { members } = this.state;

    const finalKeys = [];
    const dd = members.flatMap((x) => { return x; });


    for (let i = 0; i < dd.length; i += 1) {
      finalKeys.push(dd[i].id);
    }


    fetch(`${URL.APIURL}/addCommittee`, {
      method: 'POST',
      headers: {
        Accept: 'application/json',
        'Content-Type': 'application/json',
        'Access-Control-Allow-Origin': '*',
      },
      credentials: 'include',
      body: JSON.stringify({
        internalUserIds: finalKeys,
       }),
})
      .then((res) => { return res.json(); })
      .then(
        (res) => {
          if (res.status === 200) {
            toast.success(res.message, {
              position: 'top-center',
              autoClose: 5000,
              hideProgressBar: true,
              closeOnClick: true,
              pauseOnHover: true,
              draggable: false,
              });

          this.setState({
            internalUserData: res.internalUserData,
          });

           this.props.history.push('/manageCommittee');
           } else if (res.status === 300) {
            toast.error(res.message, {
              position: 'top-center',
              autoClose: 5000,
              hideProgressBar: true,
              closeOnClick: true,
              pauseOnHover: true,
              draggable: false,
              });
     } else if (res.status === 401) {
            error({
              title: `Session expired.${res.message}`,
              content: '',
              onOk() {
                that.props.history.push('/signIn');
              },

            });
          }
        },

);
  }

  renderTableData() {
    const { data, committeeMembers } = this.state;
    const membersKeys = [];

    const membersWithoutArray = committeeMembers.flatMap((x) => { return x; });
    for (let i = 0; i < membersWithoutArray.length; i += 1) {
      membersKeys.push(membersWithoutArray[i].id);
    }

    // var dataKeys =[];
    // for(var j=0;j<data.length;j++){
    //   dataKeys.push(data[j].id)
    // }

    const finalData = data.filter((dataToRender) => {
      return !membersKeys.includes(dataToRender.id);
      });

        // this.setState({
        //   finalData:finalData.length
        // })
    return finalData.map((item, i) => {
      return {
        key: item.id,
        name: `${item.firstName } ${ item.lastName}`,
        email: item.email,
        expertise: item.expertise === undefined ? <span>N/A</span> : item.expertise,
        add: <Checkbox ref={`ref_${ i}`} onClick={() => { return this.handleCheckBoxClick(item.id, i); }} />,
     };
    });
  }

  render() {
    document.title = `  Add Member | ${AllMessage.PageTitle}`;

    return (
      <div>
        <BreadcrumbDemo propertyProps={this.props} />
        <Card className="cardcss" title="Eligible Users">
          <div className="gx-mb-3" />
          <Table
            className="gx-table-responsive"
            columns={this.handle()}
            dataSource={this.renderTableData()}
          />
        </Card>

        <Card
          className="cardcss"
          title="Added Members"
          extra={(
            <span>
Number of members to be added :
              <b>{10 - this.state.committeeLength - this.state.keyLength}</b>
            </span>
)}
        >
          <Table
            className="gx-table-responsive"
            columns={addedMember}
            dataSource={this.dataFromAboveDataTable()}
            pagination={false}
          />
          <br />

          <Button type="primary" disabled={this.state.disabledButton} onClick={this.handleAddCommittee}>Add Committee Member(s)</Button>

        </Card>
      </div>
    );
  }
}


export default AddMember;

