import React,{Component}  from 'react';
import {
  Card,Button,Input,Space,Table,Modal,Icon,Form
 } from 'antd';
//  import moment from 'moment';
 import { toast } from 'react-toastify';
//  import URL from '../../env/environmentVar';
 import BreadcrumbDemo from '../BreadcrumbDemo';
 import AllMessage from '../AllMessages';
 import 'react-toastify/dist/ReactToastify.css';
 import queryString from 'query-string';


const {Search,TextArea} = Input
const FormItem = Form.Item;




 export class EditAnnouncement extends Component {



    state = {
      announcementTitle:'',
      announcementDescription:'',
      uid:null

    };

  componentWillMount()
  {
    const UrlQueryStrings = this.props.location.search;
    const queryValues = queryString.parse(UrlQueryStrings);

    console.log('&&&&&&&&&&&&&&&&&&&&&&&&&&&&&&&&&&&&&&&&&&&')
    console.log(queryValues.id); 

    this.setState({uid:queryValues.id})

  }
componentDidMount(){
  console.log("************id*******",this.state.uid)
  //call api and pass id
}

  handleCancel_EditAnnouncement=()=>{

    console.log('on cancel click of edit announ');
    this.props.history.push('/manageAnnouncement')
  }

  handleSubmit_EditAnnouncement=()=>{
      //call api here to post announcement
      //show toaster message if status==200

    toast.success('Announcement updated successfully', {
      position: 'top-center',
      autoClose: 3000,
      hideProgressBar: true,
      closeOnClick: true,
      pauseOnHover: true,
      draggable: false,
      });


      setTimeout(()=>{
        this.props.history.push('/manageAnnouncement')
      },3000)

  }

  handleInputChange=(event)=>{

    this.setState({
      [event.target.name] : event.target.value
    })
  }
  render() {
    document.title = `Edit Announcement | ${AllMessage.PageTitle}`;
    const { getFieldDecorator } = this.props.form;

    const formItemLayout = {
      labelCol: {
        xs: { span: 24 },
        sm: { span: 8 },
      },
      wrapperCol: {
        xs: { span: 24 },
        sm: { span: 16 },
      },
    };
    const tailFormItemLayout = {
      wrapperCol: {
        xs: {
          span: 24,
          offset: 0,
        },
        sm: {
          span: 16,
          offset: 8,
        },
      },
    };
    return (
      <div>
        <BreadcrumbDemo propertyProps={this.props} />
        <Card className="cardcss" title={<b>Announcements:Edit</b>}>


        <Card 
        type="inner" 
        title={
        <FormItem {...formItemLayout}
              label="Title : ">
                {getFieldDecorator('announcementTitle', {
                rules: [{ required: true, message: AllMessage.AnnouncementTitleValidation }],

            })(

                <Input name="announcementTitle" onChange={this.handleInputChange}  setfieldsvalue={this.state.announcementTitle}/>,
                )}

               </FormItem>}>

        <FormItem
             {...formItemLayout}
              label="Description : "
            >
             {getFieldDecorator('announcementDescription', {
                rules: [{ required: true, message: AllMessage.AnnouncementDescValidation },

              ],

            })(
                  <TextArea name="announcementDescription" setfieldsvalue={this.state.announcementDescription} onChange={this.handleInputChange}  style={{height:200,backgroundColor:'transparent'}}/>
            )}
            </FormItem>
        <FormItem {...tailFormItemLayout}>
        <Button href='/manageAnnouncement'>Cancel</Button>

        {/* on submit pass call api to update */}
        <Button type="primary" onClick={this.handleSubmit_EditAnnouncement}>Update</Button>
        </FormItem>
        {/* </div> */}

      </Card> 
    </Card>

      </div>
    );
  }
}
