import React, { Component } from 'react';
import {
 Card, Table, Button, Modal, message, Divider,
 } from 'antd';
import moment from 'moment';
import { toast } from 'react-toastify';
import URL from '../../env/environmentVar';
import BreadcrumbDemo from '../BreadcrumbDemo';
import AllMessage from '../AllMessages';
import 'react-toastify/dist/ReactToastify.css';

const { confirm } = Modal;
const { error } = Modal;

let that;
const columns = [
  {
 title: <div style={{ fontSize: 14 }}><b>Name</b></div>, dataIndex: 'name', key: 'name', ellipsis: true,
},
  {
 title: <div style={{ fontSize: 14 }}><b>Role</b></div>, dataIndex: 'role', key: 'role', ellipsis: true,
 },
  {
 title: <div style={{ fontSize: 14 }}><b>Status</b></div>, dataIndex: 'status', key: 'status', ellipsis: true,
 },
  { title: <div style={{ fontSize: 14 }}><b>Action</b></div>, dataIndex: 'action', key: 'action' },
];
export class UserManagementCoordinator extends Component {
  constructor(props) {
    super(props);

    this.state = {
      data: [],

    };
    that = this;
    this.handleButtonClick = this.handleButtonClick.bind(this);
  }


  componentDidMount() {
  fetch(`${URL.APIURL}/getAllCommitteeMember`, {
      method: 'POST',
      headers: {
        Accept: 'application/json',
        'Content-Type': 'application/json',
        'Access-Control-Allow-Origin': '*',
      },
      credentials: 'include',
      body: JSON.stringify({
       }),
})
      .then((res) => { return res.json(); })
      .then(
        (res) => {
          if (res.status === 200) {
            this.setState({
              data: res.committeeMemberData,
            });
           } else if (res.status === 300) {
            toast.error(res.message, {
              position: 'top-center',
              autoClose: 5000,
              hideProgressBar: true,
              closeOnClick: true,
              pauseOnHover: true,
              draggable: false,
              });
          } else if (res.status === 401) {
            error({
              title: AllMessage.SessionExpired + res.message,
              content: '',
              onOk() {
                that.props.history.push('/signIn');
              },

            });
          } else if (res.status === 500) {
            this.props.history.push('/serverError');
          } else if (res.status === 400) {
            // bad request
            this.props.history.push('/badRequest');
          } else if (res.status === 403) {
            // forbidden request
            this.props.history.push('/forbiddenRequest');
          }
        },

);
  }

  showConfirm(id, firstname, lastname) {
    confirm({
      title: `Are you sure you want to delete ${' '}${ firstname }${' '}${lastname}?`,
     // content: 'Some descriptions',
      onOk() {
        fetch(`${URL.APIURL}/deleteCommiteeMember`, {
          method: 'POST',
          headers: {
            Accept: 'application/json',
            'Content-Type': 'application/json',
            'Access-Control-Allow-Origin': '*',
          },
          credentials: 'include',
          body: JSON.stringify({
            committeeMemberId: id,
           }),
    })
          .then((res) => { return res.json(); })
          .then(
            (res) => {
              if (res.status === 200) {
                const removeCommitteeMember = [];
                removeCommitteeMember.push(res.updatedCommitteeMember[0].id);

                const resultAfterRemovalOfData = that.state.data.filter((data) => {
                  return !removeCommitteeMember.includes(data.id);
                });

                that.setState({
                  data: resultAfterRemovalOfData,
                }, () => {
                  toast.success(res.message, {
                    position: 'top-center',
                    autoClose: 5000,
                    hideProgressBar: true,
                    closeOnClick: true,
                    pauseOnHover: true,
                    draggable: false,
                    });
});


              //  this.setState({
              //   data:res.updatedInternalUser
              //  })
               } else if (res.status === 300) {
                toast.error(res.message, {
                  position: 'top-center',
                  autoClose: 5000,
                  hideProgressBar: true,
                  closeOnClick: true,
                  pauseOnHover: true,
                  draggable: false,
                  });
} else if (res.status === 401) {
                error({
                  title: AllMessage.SessionExpired + res.message,
                  content: '',
                  onOk() {
                    that.props.history.push('/signIn');
                  },

                });
              } else if (res.status === 500) {
                this.props.history.push('/serverError');
              } else if (res.status === 400) {
                // bad request
                this.props.history.push('/badRequest');
              } else if (res.status === 403) {
                // forbidden request
                this.props.history.push('/forbiddenRequest');
              }
            },

    );
      },
      onCancel() {
      },
    });
  }


  handleButtonClick() {
    this.props.history.push('/addMember');
  }

  renderTableData() {
    const { data } = this.state;

    return data.map((item, index) => {
      const createdAtAfterChange = moment(item.createdAt);
      const createdAt = createdAtAfterChange.format('DD-MM-YYYY');
      return {
        key: index,
        name: `${item.firstName } ${ item.lastName}`,
        role: item.role === 3 ? 'Internal reviewer' : 'Internal reviewer ',
        status: item.isVerified === true ? 'Verified' : 'Pending',
        action: [<a href={`/viewCommitteeMember?id=${item.id}`}>View</a>, <Divider style={{ backgroundColor: 'grey' }} type="vertical" />,
          <a tabIndex="0" role="button" onKeyDown={this.handleClick} onClick={() => { return this.showConfirm(item.id, item.firstName, item.lastName); }}>Remove</a>],

        description:
        [<div style={{ display: 'flex', marginBottom: '1.5%' }}>
          <div style={{ width: '50%' }}>
            {' '}
            <span style={{ wordBreak: 'break-all' }}>
              <b>Email : </b>
              {item.email === undefined ? <span> N/A</span> : item.email}
            </span>
          </div>
          <div style={{ width: '50%' }}>
            {' '}
            <span style={{ wordBreak: 'break-all' }}>
              <b>Created at : </b>
              {item.createdAt === undefined ? <span> N/A</span> : createdAt}
            </span>
          </div>

         </div>,
          <div style={{ display: 'flex', marginBottom: '1.5%' }}>
            <div style={{ width: '50%' }}>
              {' '}
              <span style={{ wordBreak: 'break-all' }}>
                <b>Added on : </b>
                {' '}
                {item.addedOn === undefined ? <span> N/A</span> : item.addedOn}
              </span>
            </div>

          </div>,


      ],
      };
    });
  }

  render() {
    document.title = `Manage Committee | ${AllMessage.PageTitle}`;

    return (
      <div>
        <BreadcrumbDemo propertyProps={this.props} />
        <Card className="cardcss" title={<b>Committee</b>} extra={<Button onClick={this.handleButtonClick} type="primary">Add Committee Members</Button>}>
          <Table
            className="gx-table-responsive"
            columns={columns}
            expandedRowRender={(record) => {
            return (
              <p style={{ margin: 0 }}>
                {record.description}

              </p>
            );
            }}
            dataSource={this.renderTableData()}
          />
        </Card>
      </div>
      );
  }
}
export default UserManagementCoordinator;

