              {' '}
              {
                    this.state.serverSideValidationError.length !== 0
                    && this.state.serverSideValidationError.map((data) => {
                      return (
                        <span>
                          {' '}
                          <span className="wt-color-red">
                            {data}
                            {' '}
                          </span>
                          {' '}
                          <br />
                        </span>
                      );
                      })
                                      }

            </span>
            {this.state.serverSideValidationError

              && <br />,
              <br />}

            <FormItem {...tailFormItemLayout}>
              <Button type="primary" disabled={this.state.disabledButton} htmlType="submit">Invite</Button>
            </FormItem>
          </Form>
        </Card>
      </div>
    );
  }
}


