import {
  Card, Tabs, Breadcrumb, Modal,
} from 'antd';
import React, { Component } from 'react';
import Tab1 from './Tab1';
import Tab2 from './Tab2';
import Tab3 from './Tab3';
import Tab4 from './Tab4';
import Tab5 from './Tab5';
import Tab6 from './Tab6';
import SideBar from '../../containers/Sidebar';
import '../../css/custom.css';
import AllMessage from '../AllMessages';

import CustomScrollbars from '../../util/CustomScrollbars';
import Tab7 from './Tab7';
import BreadcrumbDemo from '../BreadcrumbDemo';

let that;
const { TabPane } = Tabs;

class NewProposal extends Component {
  constructor(props) {
    super(props);

    this.state = {
      key: '1',
      proposalId: '',
      tab2: false,
      tab3: false,
      tab4: false,
      tab5: false,
      tab6: false,
      tagState: [],

    };
    that = this;
    this.handleTabChange = this.handleTabChange.bind(this);
    this.getData = this.getData.bind(this);
    this.getTagStatus = this.getTagStatus.bind(this);
  }

  static callback(key, status) {
    that.setState({
      key,
    });
    if (status.tab2 === true) {
      that.setState({
        tab2: true,
      });
    }
    if (status.tab3 === true) {
      that.setState({
        tab3: true,
      });
    }
    if (status.tab4 === true) {
      that.setState({
        tab4: true,
      });
    }
    if (status.tab5 === true) {
      that.setState({
        tab5: true,
      });
    }
    if (status.tab6 === true) {
      that.setState({
        tab6: true,
      });
    }
  }


  getData(val) {
    this.setState({
      proposalId: val,
    });
  }

  getTagStatus(a) {
    console.log('in get tag status', a);
    this.setState({
      tagState: a,
    }, () => {
    });
  }

  handleTabChange(key) {
    if (this.state.key === '1') {
      that.setState({
        key,
      });
    } else if (confirm('Are you sure you want to leave this page ?')) {
      that.setState({
        key,
      });
    } else {
      that.setState({
        key: that.state.key,
      });
    }
  }


  handleClickOfManageProposal() {
    this.props.history.push('/manageProposal');
  }

  render() {
    document.title = `New Proposal | ${AllMessage.PageTitle}`;

    return (
      <div>
        <BreadcrumbDemo propertyProps={this.props} />

        <Card className="cardcss" title={<b>New Proposal</b>}>

          <Tabs activeKey={that.state.key} onTabClick={this.handleTabChange}>
            <TabPane tab={<b>Submission</b>} key="1"><Tab1 /></TabPane>
            <TabPane tab={<b>Titles and abstract</b>} key="2"><Tab2 sendData={this.getData} proposalId={this.state.proposalId} /></TabPane>
            <TabPane tab={<b>Proposal details</b>} key="3"><Tab3 sendData={this.getData} tagStatus={this.getTagStatus} proposalId={this.state.proposalId} /></TabPane>
            <TabPane tab={<b>Sources of monetary or material support</b>} key="4"><Tab4 sendData={this.getData} proposalId={this.state.proposalId} /></TabPane>
            <TabPane tab={<b>Risk assessment</b>} key="5"><Tab5 sendData={this.getData} proposalId={this.state.proposalId} /></TabPane>
            <TabPane tab={<b>Potential risks and benefits</b>} key="6"><Tab6 sendData={this.getData} proposalId={this.state.proposalId} /></TabPane>
            <TabPane tab={<b>Uploading supplementary files</b>} key="7"><Tab7 propertyProps={this.props} sendData={this.getData} proposalId={this.state.proposalId} tabsStatus={this.state} /></TabPane>
            {/* <TabPane tab="Uploading Supplementry Files" key="7"><Tab7 /></TabPane> */}
          </Tabs>

        </Card>
      </div>
    );
  }
}


export default NewProposal;

