              <span><b>Fiji Health Research Portal</b></span>
            </div>

            <Form onSubmit={this.handleSubmit.bind(this)} className="gx-login-form gx-form-row0">
              <FormItem>
                {getFieldDecorator('password', {
                      rules: [{
                        required: true, message: AllMessage.PasswordValidation,
                      },
                    //   {
                    //     pattern: new RegExp('(?=.*[A-Z])(?=.*[0-9])(?=.{8,})'),
                    //     message: 'Password should be 8 character long and contain atleast one upper case and one digit.',
                    // },

                    {
                        validator: this.validateToNextPassword,
                      }],
                    })(
                      <Input type="password" placeholder="Password" />,
                    )}
              </FormItem>

              <FormItem>
                {getFieldDecorator('confirmPassword', {
                      rules: [{
                        required: true, message: AllMessage.ConfirmPasswordValidation,
                      }, {
                        validator: this.compareToFirstPassword,
                      }],
                    })(
                      <Input type="password" placeholder="Confirm Password" onBlur={this.handleConfirmBlur} />,
                    )}
              </FormItem>

              <FormItem>
                <Button type="primary" htmlType="submit">
                  <IntlMessages id="Submit" />
                </Button>
              </FormItem>
            </Form>
          </div>

        </div>
      </div>
    );
  }
}

