import { ToastContainer } from 'react-toastify';
import Sidebar from '../Sidebar/index';
import InsideHeader from '../Topbar/InsideHeader/index';
import AboveHeader from '../Topbar/AboveHeader/index';
import BelowHeader from '../Topbar/BelowHeader/index';

import Topbar from '../Topbar/index';
import { footerTextLeft, footerTextRight } from '../../util/config';
import App from '../../routes/index';
import {
  NAV_STYLE_ABOVE_HEADER,
  NAV_STYLE_BELOW_HEADER,
  NAV_STYLE_DARK_HORIZONTAL,
  NAV_STYLE_DEFAULT_HORIZONTAL,
  NAV_STYLE_DRAWER,
  NAV_STYLE_FIXED,
  NAV_STYLE_INSIDE_HEADER_HORIZONTAL,
  NAV_STYLE_MINI_SIDEBAR,
  NAV_STYLE_NO_HEADER_EXPANDED_SIDEBAR,
  NAV_STYLE_NO_HEADER_MINI_SIDEBAR,
  TAB_SIZE,
} from '../../constants/ThemeSetting';

const { Content, Footer } = Layout;

export class MainApp extends Component {
  getContainerClass = (navStyle) => {
    switch (navStyle) {
      case NAV_STYLE_DARK_HORIZONTAL:
        return 'gx-container-wrap';
      case NAV_STYLE_DEFAULT_HORIZONTAL:
        return 'gx-container-wrap';
      case NAV_STYLE_INSIDE_HEADER_HORIZONTAL:
        return 'gx-container-wrap';
      case NAV_STYLE_BELOW_HEADER:
        return 'gx-container-wrap';
      case NAV_STYLE_ABOVE_HEADER:
        return 'gx-container-wrap';
      default:
        return '';
    }
  };

  getNavStyles = (navStyle) => {
    switch (navStyle) {
      case NAV_STYLE_INSIDE_HEADER_HORIZONTAL:
        return <InsideHeader />;
      case NAV_STYLE_ABOVE_HEADER:
        return <AboveHeader />;
      case NAV_STYLE_BELOW_HEADER:
        return <BelowHeader />;
      case NAV_STYLE_FIXED:
        return <Topbar />;
      case NAV_STYLE_DRAWER:
        return <Topbar />;
      case NAV_STYLE_MINI_SIDEBAR:
        return <Topbar />;

      default:
        return null;
    }
  };

  getSidebar = (navStyle, width) => {
    if (width < TAB_SIZE) {
      return <Sidebar />;
    }
    switch (navStyle) {
      case NAV_STYLE_FIXED:
        return <Sidebar />;
      case NAV_STYLE_DRAWER:
        return <Sidebar />;
      case NAV_STYLE_MINI_SIDEBAR:
        return <Sidebar />;
      case NAV_STYLE_NO_HEADER_MINI_SIDEBAR:
        return <Sidebar />;
      case NAV_STYLE_NO_HEADER_EXPANDED_SIDEBAR:
        return <Sidebar />;
      default:
        return null;
    }
  };

  render() {
    const { match, width, navStyle } = this.props;
    return (
      <div>
        <Layout className="gx-app-layout">
          {this.getSidebar(navStyle, width)}
          <Layout>
            {this.getNavStyles(navStyle)}
            <Content className={`gx-layout-content ${this.getContainerClass(navStyle)} `}>
              <App match={match} />
              <Footer className="footer">
                <div className="gx-layout-footer-content ">
                  {/* {width<=510? <span className="leftFloat"><Tooltip placement="top" title="Copyright Ministry of Health FIJI © 2019"><b>{footerTextLeft}</b></Tooltip></span>: <span style={{float:'left'}}><b>{footerTextLeft}</b></span>} */}
                  <span className="leftFloat"><Tooltip placement="top" title="Copyright Ministry of Health FIJI © 2019"><b>{footerTextLeft}</b></Tooltip></span>
                  {/* {width<=510?<span className="leftRight"> <a style={{color:'black !important'}} href="https://www.wishtreetech.com/"><Tooltip placement="top" title="Developed By Wishtree Technologies"><b>{footerTextRight}</b></Tooltip>
</a></span>:<span style={{float:'right'}}> <a style={{color:'black !important'}} href="https://www.wishtreetech.com/"><b>{footerTextRight}</b></a></span>}  */}
                  <span className="leftRight">
                    {' '}
                    <a style={{ color: 'black !important' }} target="_blank" href="https://www.wishtreetech.com/">
                      <Tooltip placement="top" title="Developed By Wishtree Technologies"><b>{footerTextRight}</b></Tooltip>
                    </a>

                  </span>

                </div>


              </Footer>
            </Content>
          </Layout>
        </Layout>
      </div>
    );
  }
}

const mapStateToProps = ({ settings }) => {
  const { width, navStyle } = settings;
  return { width, navStyle };
};
export default connect(mapStateToProps)(MainApp);

import React from "react";
import { connect } from "react-redux";
import URLSearchParams from "url-search-params";
import { Redirect, Route, Switch } from "react-router-dom";
import { LocaleProvider, Spin } from "antd";
import { ToastContainer } from "react-toastify";
import { IntlProvider } from "react-intl";
import MainApp from "./MainApp";
import SignIn from "../SignIn";
import SignUp from "../SignUp";
import { setInitUrl, userSignIn } from "../../appRedux/actions/Auth";
import {
  onLayoutTypeChange,
  onNavStyleChange,
  setThemeType,
} from "../../appRedux/actions/Setting";
import ConfirmAccount from "../../MyComponents/ConfirmAccount";
import ConfirmInvestigatorAccount from "../../MyComponents/ConfirmInvestigatorAccount";
import URL from "../../env/environmentVar";

import {
  LAYOUT_TYPE_BOXED,
  LAYOUT_TYPE_FRAMED,
  LAYOUT_TYPE_FULL,
  NAV_STYLE_ABOVE_HEADER,
  NAV_STYLE_BELOW_HEADER,
  NAV_STYLE_DARK_HORIZONTAL,
  NAV_STYLE_DEFAULT_HORIZONTAL,
  NAV_STYLE_INSIDE_HEADER_HORIZONTAL,
  THEME_TYPE_DARK,
} from "../../constants/ThemeSetting";
import ForgotPassword from "../../MyComponents/ForgotPassword";
import ResetPassword from "../../MyComponents/ResetPassword";

let that;

class App extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      isLoading: true,
    };
    that = this;
  }

  setLayoutType = (layoutType) => {
    if (layoutType === LAYOUT_TYPE_FULL) {
      document.body.classList.remove("boxed-layout");
      document.body.classList.remove("framed-layout");
      document.body.classList.add("full-layout");
    } else if (layoutType === LAYOUT_TYPE_BOXED) {
      document.body.classList.remove("full-layout");
      document.body.classList.remove("framed-layout");
      document.body.classList.add("boxed-layout");
    } else if (layoutType === LAYOUT_TYPE_FRAMED) {
      document.body.classList.remove("boxed-layout");
      document.body.classList.remove("full-layout");
      document.body.classList.add("framed-layout");
    }
  };

  setNavStyle = (navStyle) => {
    if (
      navStyle === NAV_STYLE_DEFAULT_HORIZONTAL ||
      navStyle === NAV_STYLE_DARK_HORIZONTAL ||
      navStyle === NAV_STYLE_INSIDE_HEADER_HORIZONTAL ||
      navStyle === NAV_STYLE_ABOVE_HEADER ||
      navStyle === NAV_STYLE_BELOW_HEADER
    ) {
      document.body.classList.add("full-scroll");
      document.body.classList.add("horizontal-layout");
    } else {
      document.body.classList.remove("full-scroll");
      document.body.classList.remove("horizontal-layout");
    }
  };

  componentWillMount() {
    fetch(`${URL.APIURL}/isAuthenticated`, {
      // mode :'no-cors',
      method: "POST",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
        "Access-Control-Allow-Origin": "*",
      },
      credentials: "include",
    })
      .then((res) => {
        return res.json();
      })
      .then((res) => {
        console.log("res....in is authenticated", res);
        if (res.status === 200) {
          this.props.userSignIn(res.userData);
          global.user = res.userData;
          this.setState({
            isLoading: false,
          });
          if (
            window.location.pathname === "/signIn" &&
            global.user !== "" &&
            global.user !== undefined
          ) {
            this.props.history.push("/profile");
          }
        } else if (res.status === 300 && res.message === "Please login") {
          global.user = "";
          this.setState({
            isLoading: false,
          });
          if (
            window.location.pathname === "/confirmAccount" ||
            window.location.pathname === "/confirmInvestigatorAccount" ||
            window.location.pathname === "/forgotPassword"
          ) {
          } else {
            that.props.history.push("/signIn");
          }
        }
      });

    if (this.props.initURL === "") {
      this.props.setInitUrl(this.props.history.location.pathname);
    }
    const params = new URLSearchParams(this.props.location.search);

    if (params.has("theme")) {
      this.props.setThemeType(params.get("theme"));
    }
    if (params.has("nav-style")) {
      this.props.onNavStyleChange(params.get("nav-style"));
    }
    if (params.has("layout-type")) {
      this.props.onLayoutTypeChanewProposalnge(params.get("layout-type"));
    }
  }

  render() {
    const {
      match,
      location,
      themeType,
      layoutType,
      navStyle,
      locale,
      authUser,
      initURL,
    } = this.props;
    console.log("this.props.authuserrrrrrrrrrrrrrrrrrrr", authUser);
    const RestrictedRoute = ({ component: Component, authUser, ...rest }) => {
      return this.state.isLoading === true && global.user === undefined ? (
        <Spin style={{ marginLeft: "50%", marginTop: "20%" }} size="large" />
      ) : (
        <Route
          {...rest}
          render={(props) => {
            return that.props.authUser ? (
              <Component {...props} />
            ) : (
              <Redirect
                to={{
                  pathname: "/signIn",
                  state: { from: props.location },
                }}
              />
            );
          }}
        />
      );
    };
    if (themeType === THEME_TYPE_DARK) {
      document.body.classList.add("dark-theme");
    }
    if (location.pathname === "/") {
      return <Redirect to="/signIn" />;
    }
    this.setLayoutType(layoutType);

    this.setNavStyle(navStyle);

    return (
      <LocaleProvider>
        <IntlProvider>
          <Switch>
            <Route exact path="/signIn" component={SignIn} />
            <Route exact path="/signUp" component={SignUp} />
            <Route exact path="/confirmAccount" component={ConfirmAccount} />
            <Route exact path="/forgotPassword" component={ForgotPassword} />
            <Route exact path="/resetPassword" component={ResetPassword} />
            <Route
              exact
              path="/confirmInvestigatorAccount"
              component={ConfirmInvestigatorAccount}
            />
            {/* {!user? <Route component={Page404} />:null} */}
            {/* <Route path={`${match.url}`}
            component={MainApp} /> */}
            <RestrictedRoute
              //  path={`${match.url}`}
              //  user={user}
              component={MainApp}
            />
          </Switch>
        </IntlProvider>

        <ToastContainer />
      </LocaleProvider>
    );
  }
}

const mapStateToProps = ({ settings, auth }) => {
  const { locale, navStyle, themeType, layoutType } = settings;
  const { authUser, initURL } = auth;
  return {
    locale,
    navStyle,
    themeType,
    layoutType,
    authUser,
    initURL,
  };
};
export default connect(mapStateToProps, {
  setInitUrl,
  setThemeType,
  onNavStyleChange,
  onLayoutTypeChange,
  userSignIn,
})(App);

