const passport = require('passport');
const LocalStrategy = require('passport-local').Strategy;
const bcrypt = require('bcryptjs');

passport.serializeUser((user, done) => {
  done(null, user);
});

passport.deserializeUser((req, user, done) => {
  if (user.role === 6) {
    Investigator.findOne({ id: user.id }, (err, userData) => {
      done(err, userData);
    });
  } else if (user.role === 1 || user.role === 2 || user.role === 3 || user.role === 4 || user.role === 5) {
    InternalUser.findOne({ id: user.id }, (err, userData) => {
      done(err, userData);
    });
  } else {
    User.findOne({ id: user.id }, (err, userData) => {
      done(err, userData);
    });
  }
});

passport.use(new LocalStrategy({
  usernameField: 'username',
  passwordField: 'password',
  passReqToCallback: true,
},
((req, username, password, done) => {
  Logger.debug('password LocalStrategy');
  Logger.verbose(req.body);
  User.findOne({ email: req.body.username.toLowerCase(), isActive: true }, (err, user) => {
    if (err) {
      Logger.error(`Passport : at user.findOne ${err}`);
      return done(err);
    }
    if (!user) {
      Logger.warn({ message: 'Incorrect Username' });
      return done(null, false, { message: 'Incorrect Username' });
    }
    if (!user.password) {
      Logger.warn({ message: 'Incorrect Username' });
      return done(null, false, { message: 'Incorrect Password' });
    }
    bcrypt.compare(password, user.password, (bcryptErr, res) => {
      if (bcryptErr) {
        Logger.warn('password bcrypt Error');
        return done(bcryptErr);
      }
      if (!res) {
        Logger.warn({ message: 'Incorrect Username' });
        return done(null, false, { message: 'Incorrect Password' });
      }
      if (user.role === 6) {
        Investigator.findActiveByCriteria({ email: user.email.toLowerCase(), isVerified: true }, (findActiveByCriteriaErr, investigator) => {
          if (err) {
            Logger.error(`AuthController.processLogin at req.logIn ${findActiveByCriteriaErr}`);
            return res.send({ status: 300, message: 'Something went wrong. Please try again later.', user: null });
          }
          Logger.info({ message: 'Logged In Successfully' });
          return done(null, investigator, { message: 'Logged In Successfully' });
        });
      } else if (user.role === 1 || user.role === 2 || user.role === 3 || user.role === 4 || user.role === 5) {
        InternalUser.findActiveByCriteria({ email: user.email.toLowerCase(), isVerified: true }, (findActiveByCriteriaErr, internalUser) => {
          if (err) {
            Logger.error(`AuthController.processLogin at req.logIn ${findActiveByCriteriaErr}`);
            return res.send({ status: 300, message: 'Something went wrong. Please try again later.', user: null });
          }
          Logger.info({ message: 'Logged In Successfully' });
          return done(null, internalUser, { message: 'Logged In Successfully' });
        });
      } else {
        Logger.info({ message: 'Logged In Successfully' });
        return done(null, user, { message: 'Logged In Successfully' });
      }
    });
  });
})));

